import inputComp from "@/components/Index/Input.vue";
import { apiRegister, apiGetCaptchCode } from "@/api/User.js";
export default {
  components: {
    inputComp: inputComp
  },
  created: function created() {
    this.getParams();
  },
  computed: {
    ifPassword: function ifPassword() {
      return this.togglePassword ? "password" : "text";
    }
  },
  data: function data() {
    return {
      togglePassword: true,
      account: "",
      password: "",
      imgCode: "",
      phone: "",
      confirmPassword: "",
      showcode: true,
      InvitationCode: "",
      url: process.env.VUE_APP_API_BASE_URL + "/User/GetCaptchCode",
      GetCaptchCodeIndex: 0,
      isCheck: true
    };
  },
  methods: {
    getParams: function getParams() {
      var params = this.$route.query.InviterId;
      this.InvitationCode = params;
    },
    gojump: function gojump() {
      this.$router.push("/");
    },
    //验证码
    recode: function recode() {
      this.url = process.env.VUE_APP_API_BASE_URL + "/User/GetCaptchCode?" + this.GetCaptchCodeIndex;
      this.GetCaptchCodeIndex++;
      new Date().getTime();
    },
    sendRegister: function sendRegister() {
      var _this = this;

      var data = {
        Name: this.account,
        Password: this.password,
        ConfirmPassword: this.confirmPassword,
        PhoneNumber: this.phone,
        ImgCode: this.imgCode,
        InvitationCode: this.InvitationCode
      };

      if (this.account == "") {
        var m = this.$message({
          message: this.$t("AccountEmpty"),
          duration: 1000,
          type: "error",
          center: true
        });
        setTimeout(function () {
          return m.close();
        }, 1200);
        return;
      }

      ;

      if (this.password == "") {
        var _m = this.$message({
          message: this.$t("PasswordBlank"),
          duration: 1000,
          type: "error",
          center: true
        });

        setTimeout(function () {
          return _m.close();
        }, 1200);
        return;
      }

      ;

      if (this.phone == "") {
        var _m2 = this.$message({
          message: this.$t("PhoneBlank"),
          duration: 1000,
          type: "error",
          center: true
        });

        setTimeout(function () {
          return _m2.close();
        }, 1200);
        return;
      }

      ;

      if (this.imgCode == "") {
        var _m3 = this.$message({
          message: this.$t("VerificationCodeEmpty"),
          duration: 1000,
          type: "error",
          center: true
        });

        setTimeout(function () {
          return _m3.close();
        }, 1200);
        return;
      }

      ;

      if (!this.isCheck) {
        var _m4 = this.$message({
          message: this.$t("AgreePrivacy"),
          duration: 1000,
          type: "error",
          center: true
        });

        setTimeout(function () {
          return _m4.close();
        }, 1200);
        return;
      }

      apiRegister(data).then(function (res) {
        if (res.AccessToken) {
          var _m5 = _this.$message({
            message: _this.$t('RegisteredSuccessfully'),
            duration: 3000,
            center: true,
            type: "success"
          });

          setTimeout(function () {
            return _m5.close();
          }, 1200);
          setTimeout(function () {
            _this.$router.push("/Login");
          }, 3000);
        } else if (Math.abs(res.Code) >= 600) {
          var _m6 = _this.$message({
            message: res.Message,
            duration: 3000,
            center: true,
            type: "error"
          });

          setTimeout(function () {
            return _m6.close();
          }, 1200);
        }
      });
    },
    getAccount: function getAccount(res) {
      this.account = res;
    },
    GetPassword: function GetPassword(res) {
      this.password = res;
    },
    getConfirmPassword: function getConfirmPassword(res) {
      this.confirmPassword = res;
    },
    getPhone: function getPhone(res) {
      this.phone = res;
    },
    getImgCode: function getImgCode(res) {
      this.imgCode = res;
    }
  }
};
