import back from "@/components/Botton/back.vue";
import model from "@/components/model";
import { apiGetRechargeInfo } from "@/api/Recharge.js";
export default {
  components: {
    back: back,
    model: model
  },
  created: function created() {
    this.payName = this.payList[0];
    this.getApiGetRechargeInfo();
  },
  data: function data() {
    return {
      payList: ["USDT"],
      payName: "",
      address: "",
      protocolTab: ["ERC20"],
      tabIndex: 0,
      isOpen: false,
      exchangeRate: ""
    };
  },
  methods: {
    // 取得地址
    getApiGetRechargeInfo: function getApiGetRechargeInfo() {
      var _this = this;

      apiGetRechargeInfo("", true).then(function (res) {
        _this.address = res.Address;
        _this.exchangeRate = res.UsdtPrice;
      });
    },
    // 選擇支付方式
    selectPay: function selectPay(index) {
      this.payName = this.payList[index];
      this.isOpen = false;
    },
    // 複製地址
    copyAddress: function copyAddress() {
      var TextRange = document.createRange();
      TextRange.selectNode(document.getElementById("Address"));
      var sel = window.getSelection();
      sel.removeAllRanges();
      sel.addRange(TextRange);
      document.execCommand("copy");
    }
  }
};
