import back from "@/components/Botton/back.vue";
import { apiClubUpload, apiGetClubAdBaseConfing, apiSubmitClubAd } from "@/api/Club";
export default {
  components: {
    back: back
  },
  data: function data() {
    return {
      noMission: false,
      typeList: [{
        typeName: this.$t("Top"),
        price: 0
      }, {
        typeName: this.$t("Popular"),
        price: 0
      }],
      checkList: [],
      submitClubAdData: {
        ClubId: "",
        Type: "",
        Day: "",
        Img: "",
        Amount: "",
        UName: ""
      },
      upImg: false,
      recommendPrice: "",
      popularPrice: ""
    };
  },
  created: function created() {
    this.submitClubAdData.ClubId = localStorage.getItem("ClubId");
    this.submitClubAdData.UName = localStorage.getItem("uname");
  },
  mounted: function mounted() {
    var _this2 = this;

    apiGetClubAdBaseConfing("", true).then(function (res) {
      _this2.recommendPrice = res.Data.RecommendPrice;
      _this2.popularPrice = res.Data.PopularPrice;
      _this2.typeList[0].price = res.Data.MaxDay * res.Data.RecommendPrice;
      _this2.typeList[1].price = res.Data.MaxDay * res.Data.PopularPrice;
      _this2.submitClubAdData.Day = res.Data.MaxDay;
    });
  },
  methods: {
    // 图片上传功能
    tirggerFile: function tirggerFile(event) {
      var file = event.target.files[0];
      var imgSize = file.size;
      var param = new FormData(); // 创建form对象

      param.append('file', file, file.name); // 通过append向form对象添加数据

      param.append('type', '1'); // 添加form表单中其他数据
      // console.log(param.get('file')) // FormData私有类对象，访问不到，可以通过get判断值是否传进去

      if (!event.target.files.length) return;

      if (imgSize / 1024 > 10000) {
        this.$message({
          message: this.$t("PictureIsTooBig"),
          type: "warning",
          center: true,
          duration: 1200
        });
      } else {
        this.imgPreview(file);
      }
    },
    // 获取图片
    imgPreview: function imgPreview(file, callback) {
      var _this = this; // this指向问题，所以在外面先定义


      var config = {
        headers: {
          'Content-Type': 'multipart/form-data'
        },
        contentType: false,
        //required
        processData: false // required

      }; // 判断是否支持FileReader

      if (!file || !window.FileReader) return; // 创建一个reader

      var reader = new FileReader(); // 将图片转成base64格式

      reader.readAsDataURL(file);

      reader.onloadend = function () {
        var result = this.result;
        var img = new Image();
        img.src = result; // console.log('============未压缩图片===========')

        img.onload = function () {
          var data = _this.compress(img);

          _this.imgUrl = result;

          var blob = _this.dataURItoBlob(data); // console.log('*******base64转blob对象******')


          var param = new FormData(); // 创建form对象

          param.append('file', blob, file.name); // 通过append向form对象添加数据

          param.append('type', "clubad");
          apiClubUpload(param, config).then(function (res) {
            if (res.Success) {
              _this.$message({
                message: _this.$t("UploadSuccessfully"),
                type: "success",
                center: true,
                duration: 1200
              }); //顯示圖片


              _this.upImg = true;
              document.querySelector('#demo').setAttribute('src', result);
              _this.submitClubAdData.Img = file.name;
            } else {
              _this.$message({
                message: _this.$t("UploadFailed"),
                type: "error",
                center: true,
                duration: 1200
              });
            }
          });
        };
      };
    },
    // 压缩图片
    compress: function compress(img) {
      var canvas = document.createElement('canvas');
      var ctx = canvas.getContext('2d'); // let initSize = img.src.length

      var width = img.width;
      var height = img.height;
      canvas.width = width;
      canvas.height = height; // 铺底色

      ctx.fillStyle = '#fff';
      ctx.fillRect(0, 0, canvas.width, canvas.height);
      ctx.drawImage(img, 0, 0, width, height); // 进行最小压缩

      var ndata = canvas.toDataURL('image/jpeg', 0.1); // console.log('*******压缩后的图片大小*******')

      return ndata;
    },
    dataURItoBlob: function dataURItoBlob(base64Data) {
      var byteString;
      if (base64Data.split(',')[0].indexOf('base64') >= 0) byteString = atob(base64Data.split(',')[1]);else byteString = unescape(base64Data.split(',')[1]);
      var mimeString = base64Data.split(',')[0].split(':')[1].split(';')[0];
      var ia = new Uint8Array(byteString.length);

      for (var i = 0; i < byteString.length; i++) {
        ia[i] = byteString.charCodeAt(i);
      }

      return new Blob([ia], {
        type: mimeString
      });
    },
    //廣告種類選擇
    bindCheckBox: function bindCheckBox(i) {
      this.submitClubAdData.Type = i;
      this.submitClubAdData.Amount = this.typeList[i].price;

      if (this.checkList.length > 1) {
        this.checkList.splice(0, 1);
      }

      if (this.checkList.length == 0) {
        this.submitClubAdData.Type = "";
        this.submitClubAdData.Amount = "";
      }
    },
    clubAdPutInSubmit: function clubAdPutInSubmit() {
      var _this3 = this;

      if (!this.upImg) {
        this.$message({
          message: this.$t("UploadYourClubLogo"),
          type: "error",
          center: true,
          duration: 1200
        });
      } else if (this.submitClubAdData.Type === "") {
        this.$message({
          message: this.$t("PleaseSelectAd"),
          type: "error",
          center: true,
          duration: 1200
        });
      } else {
        apiSubmitClubAd(this.submitClubAdData, true).then(function (res) {
          if (res.Success) {
            _this3.$message({
              message: res.Message,
              type: "success",
              center: true,
              duration: 1200
            });

            _this3.$router.push("/myAuthority");
          } else {
            _this3.$message({
              message: res.Message,
              type: "error",
              center: true,
              duration: 1200
            });
          }
        });
      }
    },
    deposit: function deposit() {
      this.$router.push("/recharge");
    }
  }
};
