import back from "@/components/Botton/back.vue";
import { apiGetClubActivityMatchResult } from "@/api/ClubActivitys.js";
export default {
  components: {
    back: back
  },
  data: function data() {
    return {
      clubId: "",
      matchResultObj: [],
      noData: false,
      isLoading: false,
      // v-infinite-scroll 套件
      busy: false,
      immediate: false,
      indexNum: 1,
      totalPages: undefined
    };
  },
  created: function created() {
    localStorage.setItem("categoryIndex", 1);
    this.clubId = localStorage.getItem("ClubId");
    this.getMatchResult();
  },
  methods: {
    getMatchResult: function getMatchResult() {
      var _this = this;

      var data = {
        index: this.indexNum,
        count: 10
      };
      this.isLoading = true;
      this.noData = false;
      apiGetClubActivityMatchResult(data, true).then(function (res) {
        if (Math.abs(res.Code) >= 600) {
          _this.noData = true;
          _this.isLoading = false;
        } else {
          _this.matchResultObj = res.Data.Data;
          _this.totalPages = res.Data.TotalPages;
          _this.isLoading = false;
          _this.noData = false;
        }
      });
    },
    goResultDetail: function goResultDetail(item) {
      localStorage.setItem("AcvtiveId", item.Id);
      this.$router.push("/ActivityMatchResultDetail");
    },
    // 無限加載
    load: function load() {
      var _this2 = this;

      if (this.indexNum >= this.totalPages) {
        this.busy = true;
        return;
      }

      this.indexNum++;
      var data = {
        index: this.indexNum,
        count: 10
      };
      apiGetClubActivityMatchResult(data, true).then(function (res) {
        res.Data.Data.forEach(function (el) {
          _this2.matchResultObj.push(el);
        });
      });
    }
  },
  beforeDestroy: function beforeDestroy() {
    localStorage.setItem("categoryIndex", 1);
  }
};
