import back from "@/components/Botton/back.vue";
import { apiGetUserAddress, apiAddEditMemAddress, apiDelMemAddress } from "@/api/User.js";
import Vue from "vue";
import { Area, Toast } from "vant";
Vue.use(Area).use(Toast);
export default {
  components: {
    back: back
  },
  filters: {
    openMo: function openMo(val) {
      if (val == 1) {
        return true;
      } else {
        return false;
      }
    }
  },
  data: function data() {
    return {
      openList: [],
      value2: "",
      tan: false
    };
  },
  methods: {
    quxiao: function quxiao() {
      this.tan = false;
    },
    goBack: function goBack() {
      this.$router.go(-1);
    },
    que: function que() {
      var _this = this;

      var data = {
        AID: this.openList[0]
      };
      apiDelMemAddress(data, true).then(function (res) {
        var m = _this.$message({
          message: _this.$t("successfullyDeleted"),
          type: "error",
          duration: 1000
        });

        setTimeout(function () {
          return m.close();
        }, 1200);

        _this.$router.push("/shopList");
      });
    },
    changes: function changes() {},
    deltel: function deltel() {
      this.tan = true;
    },
    commit: function commit() {
      var _this2 = this;

      var isMo = 0;

      if (this.value2) {
        isMo = 1;
      } else {
        isMo = 0;
      }

      var data = {
        AId: this.openList[0],
        UId: "",
        UName: this.openList[1],
        UPhoneNumber: this.openList[2],
        URedeemAddress: this.openList[4],
        // IdentityCard: this.detailAdd,
        CreateTime: this.openList[5],
        IsDefault: isMo
      };
      apiAddEditMemAddress(data, true).then(function (res) {
        if (Math.abs(res.Code) >= 600) {
          _this2.$message({
            message: res.Message,
            type: "warning",
            center: true,
            duration: 3000
          });
        } else {
          var m = _this2.$message({
            message: _this2.$t("SavedSuccessfully"),
            type: "success",
            duration: 1000
          });

          setTimeout(function () {
            return m.close();
          }, 1200);

          _this2.$router.push("/shopList");
        }
      });
    }
  },
  created: function created() {
    this.openList = this.$route.query.arrs;

    if (this.openList[3] == 0) {
      return this.value2 = false;
    } else {
      return this.value2 = true;
    }
  }
};
