import NavBottom from "@/components/Main/NavBottom.vue";
import back from "../components/Botton/back.vue";
import model from "../components/model";
import { apiGetBetResponse, apiBet } from "../api/match.js";
import { betSuccess } from "@/utils/medias";
export default {
  watch: {
    betnum: function betnum() {
      if (this.betinfo.PlayType == 3 || this.betinfo.PlayType == 4 || this.betinfo.PlayType == 5 || this.betinfo.PlayType == 6) {
        this.money = (this.betnum * this.betinfo.OddsValue).toFixed(2);
      } else {
        this.money = (this.betnum * this.betinfo.OddsValue - this.betnum).toFixed(2);
      }

      this.rangeValue = parseInt(this.betnum);
    }
  },
  components: {
    NavBottom: NavBottom,
    back: back,
    model: model
  },
  created: function created() {
    // this.getMatchPlay();
    this.getMatchMorePlay();
  },
  data: function data() {
    return {
      tab1: true,
      tab2: false,
      tab3: false,
      show: false,
      showbet: false,
      betnum: 0,
      rangeValue: 0,
      rangeMaxVal: 0,
      money: "",
      playtype: 11,
      playId: "",
      betinfo: "",
      fastnum: ""
    };
  },
  methods: {
    fastbet: function fastbet(id) {
      this.fastnum = id;
      this.betnum = id;
    },
    gojump: function gojump() {
      this.$router.go(-1);
    },
    //关闭下注
    closedialog: function closedialog() {
      this.showbet = false;
    },
    showBetFn: function showBetFn(id) {
      var _this = this;

      // if (id > 0) {
      //   this.showbet = true;
      // }
      this.playId = id;

      if (this.playId == -1) {
        return;
      }

      var data = {
        playId: id,
        betType: 1
      };
      apiGetBetResponse(data, true).then(function (res) {
        if (Math.abs(res.Code) >= 600) {
          _this.$message({
            message: res.Message,
            duration: 3000,
            type: "error",
            center: true
          });
        } else {
          _this.showbet = true;
          _this.betinfo = res; // 投注金額預設最低投注金額

          _this.betnum = res.MinBetIntegral;
          _this.rangeValue = _this.betnum; // 判斷最大單筆投注金額回傳是否為-1 如是拉桿的最大值為10000

          if (res.MaxBetIntegral === -1) _this.rangeMaxVal = 10000;else _this.rangeMaxVal = res.MaxBetIntegral; // 判斷玩家餘額小於最小可投注額時，指针放置在开始位置显示最小可投注额 且不可拖动

          if (_this.betinfo.AvailableIntegral < _this.betinfo.MinBetIntegral) {
            _this.isDisable = true;
            return;
          } // 判斷玩家餘額跟回傳的MaxBetIntegral 哪個小就取哪個


          if (_this.betinfo.AvailableIntegral > res.MaxBetIntegral) {
            _this.rangeMaxVal = res.MaxBetIntegral;
            _this.isDisable = false;
          } else if (_this.betinfo.AvailableIntegral < res.MaxBetIntegral) {
            _this.rangeMaxVal = _this.betinfo.AvailableIntegral;
            _this.isDisable = false;
          } else if (_this.betinfo.AvailableIntegral = res.MaxBetIntegral) {
            _this.rangeMaxVal = _this.betinfo.AvailableIntegral;
            _this.isDisable = false;
          }
        }
      });
    },
    changeRangeVal: function changeRangeVal() {
      this.betnum = this.rangeValue;
      var tooltip = document.querySelector(".slide-tooltip");
      var rangeBtn = document.querySelector(".el-slider__bar");
      var tooltipWidth = tooltip.getBoundingClientRect().width; // 提示框最正確的寬度

      var rangeBtnOffset = rangeBtn.offsetWidth; // 拖動拉桿時拉桿條的偏移量

      var rangeBtnOffsetParent = rangeBtn.offsetParent.offsetLeft; // 拉桿條離父層左邊(父層padding-left)的偏移量

      var tolerance = tooltipWidth / 2 + (rangeBtnOffsetParent - tooltipWidth); // 公差

      if (!this.isrunTwice) {
        tooltip.style.left = tolerance + "px";
        this.isrunTwice = true;
      } else {
        tooltip.style.left = rangeBtnOffset + tolerance + "px";
      }
    },
    //投注金额
    addbet: function addbet() {
      this.betnum = this.betnum + 10;
    },
    losebet: function losebet() {
      this.betnum = this.betnum - 10;
    },
    //可赢金额
    getmoney: function getmoney() {
      this.money = this.betnum * this.betinfo.OddsValue;
    },
    //下注
    bet: function bet() {
      var _this2 = this;

      var data = {
        playId: this.playId,
        betIntegral: this.betnum,
        betType: 1
      };

      if (this.betnum >= 10) {
        apiBet(data, true).then(function (res) {
          if (res.IsBetSucceed) {
            _this2.$message({
              message: _this2.$t("BettingSuccess"),
              duration: 2500,
              type: "success",
              center: true
            });

            _this2.showbet = false;
            _this2.fastnum = ""; // 下注成功音效

            betSuccess.play();
          } else if (Math.abs(res.Code) >= 600) {
            _this2.$message({
              message: res.Message,
              duration: 1000,
              type: "error",
              center: true
            });
          }
        });
      } else {
        this.$message.error(this.$t("BetErrorMessage"));
      }
    },
    //获取赛事玩法
    getMatchPlay: function getMatchPlay() {
      this.$store.dispatch("asyncGetMatchplay", {
        id: this.playtype
      });
    },
    //更多玩法
    getMatchMorePlay: function getMatchMorePlay(id) {
      var data = {
        playTypeList: id || this.playtype
      };
      this.$store.dispatch("asyncGetMatchMorePlay", {
        data: data
      });
    },
    tabshow1: function tabshow1(id) {
      this.$store.commit("uploading", true);
      this.playtype = id;
      this.tab1 = true;
      this.tab2 = false;
      this.tab3 = false;
      this.getMatchMorePlay(id);
    },
    tabshow2: function tabshow2(id) {
      this.$store.commit("uploading", true);
      this.playtype = id;
      this.tab1 = false;
      this.tab2 = true;
      this.tab3 = false;
      this.getMatchMorePlay(id);
    },
    tabshow3: function tabshow3(id) {
      this.$store.commit("uploading", true);
      this.playtype = id;
      this.tab1 = false;
      this.tab2 = false;
      this.tab3 = true;
      this.getMatchMorePlay(id);
    }
  }
};
