import Vue from "vue";
import { Toast } from "vant";
Vue.use(Toast);
import { apiGetContactUsInfo } from "@/api/Appsetting.js";
import { apiGetMemberMatchAndUserRank } from "@/api/match.js";
import back from "@/components/Botton/back.vue";
export default {
  name: "history",
  components: {
    back: back
  },
  filters: {
    state: function state(val) {
      if (val = "已结算") {
        return 1;
      } else {
        return 2;
      }
    }
  },
  data: function data() {
    return {
      list: [this.$t('Ordinary'), this.$t('Rank'), this.$t('Challenge')],
      num: 0,
      imgSrc: "",
      showImg: false,
      nomsg: true,
      noMore: false,
      noMore1: false,
      noMore2: false,
      push: false,
      push1: false,
      push2: false,
      usualList: [],
      //普通赛列表1
      rankList: [],
      //排位赛列表2
      challageList: [],
      //挑战赛列表3
      letfTeam: "",
      battle: "",
      rightTeam: "",
      isLoading: false,
      //下拉
      loading: false,
      //上拉
      finished: false,
      //是否已加载完所有数据
      indexNum: 1,
      show: true,
      states: true,
      states2: false,
      IndexNum: 1,
      busy: false,
      typeNum: 1,
      typeNum2: 1,
      lang: ""
    };
  },
  methods: {
    goBack: function goBack() {
      this.$router.go(-1);
    },
    getNum: function getNum(index) {
      this.num = index;

      if (this.num == 0) {
        this.typeNum = 1;
        this.IndexNum = 1;
        this.usualList = [];
        this.load();
      }

      if (index == 1) {
        this.typeNum = 2;
        this.IndexNum = 1;
        this.rankList.splice(0);
        this.load();
      }

      if (index == 2) {
        this.typeNum = 3;
        this.IndexNum = 1;
        this.challageList.splice(0);
        this.load();
      }
    },
    load: function load() {
      var _this = this;

      this.busy = true;
      var data = {
        index: this.IndexNum,
        count: 10,
        type: this.typeNum //普通赛

      };

      if (data.type == 1) {
        apiGetMemberMatchAndUserRank(data, true).then(function (res) {
          if (res.Data.length > 7) {
            _this.push = true;
          }

          if (res.Data.length == 0) {
            _this.busy = true;
            _this.push = false;
            _this.noMore = true;
          } else {
            for (var i = 0; i < res.Data.length; i++) {
              _this.usualList.push(res.Data[i]);

              _this.busy = false;
            }

            _this.IndexNum++;
          }
        });
      } else if (data.type == 2) {
        this.push1 = true;
        apiGetMemberMatchAndUserRank(data, true).then(function (res) {
          if (res.Data.length == 0) {
            _this.push1 = false;
            return;
          }

          for (var i = 0; i < res.Data.length; i++) {
            _this.rankList.push(res.Data[i]);

            _this.busy = false;
          }

          _this.IndexNum++;

          if (res.TotalPages == _this.IndexNum - 1) {
            _this.busy = true;
          }
        });
      } else if (data.type == 3) {
        this.push2 = true;
        apiGetMemberMatchAndUserRank(data, true).then(function (res) {
          if (res.Data.length == 0) {
            _this.push2 = false;
            return;
          }

          for (var i = 0; i < res.Data.length; i++) {
            _this.challageList.push(res.Data[i]);

            _this.busy = false;
          }

          _this.IndexNum++;

          if (res.TotalPages == _this.IndexNum - 1) {
            _this.busy = true;
          }
        });
      }
    }
  },
  created: function created() {
    this.lang = JSON.parse(localStorage.getItem("lang")).tag;
  }
};
