import { apiGetUnreadMessageCount, apiGetMessageList } from "@/api/Message.js";
import back from "@/components/Botton/back.vue";
var isbool = true;
export default {
  name: "MyMessage",
  components: {
    back: back
  },
  data: function data() {
    return {
      msgTime: "2019-09-10",
      msgList: [],
      myCont: "",
      msgCont: "",
      myTitle: "",
      disPlay: "none",
      IndexNum: 1,
      loading: false,
      srcIcon: require("@/assets/myself/lingdang_03.png"),
      imgSrc: false,
      busy: false,
      noMore: false,
      push: false
    };
  },
  filters: {
    fileStr: function fileStr(val) {
      if (val && val.length > 19) {
        val = val.substring(0, 10) + " " + val.substring(11, 16);
      }

      return val;
    }
  },
  computed: {},
  methods: {
    goBack: function goBack() {
      this.$router.go(-1);
    },
    getId: function getId(name, title) {
      this.msgCont = name;
      this.disPlay = "block";
    },
    getMessageList: function getMessageList() {
      var _this = this;

      var data = {
        index: 1,
        count: 10
      };
      apiGetMessageList(data, true).then(function (res) {
        _this.msgList = res.Data;
      });
    },
    load: function load() {
      var _this2 = this;

      this.busy = true;
      var data = {
        index: this.IndexNum,
        count: 10
      };
      apiGetMessageList(data, true).then(function (res) {
        if (res.Data.length == 0) {
          _this2.busy = true;
          _this2.push = false; // this.noMore = true
        } else if (res.Data.length > 0) {
          for (var i = 0; i < res.Data.length; i++) {
            _this2.msgList.push(res.Data[i]);

            _this2.busy = false;
          }

          _this2.IndexNum++;
        }
      });
    },
    // 顯示更多
    showMore: function showMore(e) {
      document.querySelectorAll(".message-ul-item-div-span")[e].classList.remove("hideMore");
      document.querySelectorAll(".message-ul-item-div-span_box")[e].classList.remove("spanBox");
      document.querySelectorAll(".showMore")[e].style.display = "none";
      document.querySelectorAll(".message-ul-item")[e].classList.add("showAll");
    }
  },
  mounted: function mounted() {
    // 顯示更多
    setTimeout(function () {
      for (var i = 0; document.querySelectorAll(".message-ul-item").length > i; i++) {
        if (document.querySelectorAll(".message-ul-item-div-span")[i].clientHeight > 22) {
          document.querySelectorAll(".message-ul-item-div-span")[i].classList.add("hideMore");
          document.querySelectorAll(".showMore")[i].style.display = "block";
        }
      }
    }, 600);
  }
};
