import back from "@/components/Botton/back.vue";
import { apiJoinClub, apiGetClubRecord } from "@/api/Club.js";
import model from "@/components/model";
export default {
  name: "clubData",
  components: {
    back: back,
    model: model
  },
  data: function data() {
    return {
      selectClubId: undefined,
      isMember: 0,
      isOpen: false,
      isError: false,
      nickName: "",
      errorMsg: "",
      clubData1: {
        clubName: "",
        clubLevel: "",
        memberNum: ""
      },
      recordObj: [{
        state: this.$t('MyClubRecord'),
        // 戰
        times: "",
        tip: this.$t('TotalNumbeOfBetsOfMembers') //成员总投注次数

      }, {
        state: this.$t('Total'),
        //總
        times: "",
        tip: this.$t('TotalBetOfMembers') //成员总投注额

      }, {
        state: this.$t('Win'),
        // 贏
        times: "",
        tip: this.$t('WinOrLoseCount') //成员总输赢次数

      }, {
        state: this.$t('Lose'),
        // 負
        times: "",
        tip: this.$t('WinOrLoseCount') //成员总输赢次数

      }],
      smallText: false
    };
  },
  created: function created() {
    // 獲取使用者暱稱
    this.nickName = localStorage.getItem('nickName'); // 判斷是否有會員 1 = 有  2 = 無

    this.isMember = localStorage.getItem("isMember"); // 獲取選取的俱樂部ID

    this.selectClubId = localStorage.getItem("selectClub"); // 獲取俱樂部詳細資訊

    this.getClubRecord();
  },
  methods: {
    // 獲取俱樂部詳細資訊
    getClubRecord: function getClubRecord() {
      var _this = this;

      var data = {
        ClubId: this.selectClubId
      };
      apiGetClubRecord(data, true).then(function (res) {
        _this.clubData1.clubName = res.Data.ClubName;
        _this.clubData1.clubLevel = res.Data.LevelId;
        _this.clubData1.memberNum = res.Data.PeopleCount;
        _this.clubData1.NickName = res.Data.NickName;
        _this.recordObj[0].times = res.Data.SumBetCount;
        _this.recordObj[1].times = res.Data.SumBetAmounCount;
        _this.recordObj[2].times = res.Data.SumWinCount;
        _this.recordObj[3].times = res.Data.SumLoseCount; // 判斷投注總額位數，超過6位數的話縮小文字大小

        var digits = res.Data.SumBetAmounCount.toString().length; // 先轉成文字算位數

        if (digits >= 6) {
          _this.smallText = true;
        } else {
          _this.smallText = false;
        }
      });
    },
    // 申請加入俱樂部
    joinClub: function joinClub() {
      var _this2 = this;

      var data = {
        ClubId: this.selectClubId,
        UName: this.uname,
        Type: 0 // 0 申请进入  1 退出  2 被踢

      };
      apiJoinClub(data, true).then(function (res) {
        _this2.isError = true;
        _this2.errorMsg = res.Message;
        _this2.isOpen = false;
      });
    }
  }
};
