import back from "@/components/Botton/back.vue";
import { apiGetClubAnnouncement, apiAddOrUpClubAnnouncement } from "@/api/Club.js";
export default {
  components: {
    back: back
  },
  data: function data() {
    return {
      inputTitle: "",
      inputTextarea: "",
      id: 0,
      textLen: 0,
      maxTextLen: 200,
      doubleClick: false
    };
  },
  created: function created() {
    this.getNoticeList();
  },
  methods: {
    // 獲取公告列表
    getNoticeList: function getNoticeList() {
      var _this = this;

      var data = {
        ndex: this.indexNum,
        count: 10
      };
