        _this.inputTitle = res.Data.Data[0].Title;
        _this.inputTextarea = res.Data.Data[0].Content;
        _this.id = res.Data.Data[0].Id;
      }).then(function () {
        _this.textLen = _this.inputTextarea.length;
      });
    },
    // 字數檢測
    textcontrol: function textcontrol() {
      var len = this.inputTextarea.length;
      this.textLen = len;
    },
    // 編輯公告
    addOrEditNotice: function addOrEditNotice() {
      var _this2 = this;

      if (this.textLen > this.maxTextLen) {
        this.$message({
          message: this.$t('MoreEditableWords'),
          type: "warning",
          center: true,
          duration: 1200
        });
        return;
      }

      if (this.doubleClick == false) {
        var data = {
          Title: this.inputTitle,
          Content: this.inputTextarea,
          Status: 1,
          Id: this.id
        };
        apiAddOrUpClubAnnouncement(data, true).then(function (res) {
          if (res.Success) {
            var vm = _this2;
            var m = vm.$message({
              message: res.Message,
              duration: 3000,
              type: "success",
              center: true
            });
            setTimeout(function () {
              m.close();

              _this2.$router.push("/myClub");
            }, 2000);
          } else {
            var _vm = _this2;

            var _m = _vm.$message({
              message: res.Message,
              duration: 3000,
              type: "warning",
              center: true
            });

            setTimeout(function () {
              return _m.close();
            }, 2000);
          }
        });
      }

      this.doubleClick = true;
    }
  }
};
