import DialogComp from "./index.vue";
import { apiSign } from "@/api/User.js";
import Vue from "vue";
import { Toast } from "vant";
import FlowAd from "../FlowAd.vue";
import { apiGetFlowAdByType } from "@/api/FlowAd.js";
Vue.use(Toast);
export default {
  name: "SignIn",
  props: {
    showModal: {
      type: Boolean,
      default: false
    }
  },
  components: {
    DialogComp: DialogComp,
    FlowAd: FlowAd
  },
  filters: {
    numDay: function numDay(num) {
      if (num = null) {
        return 0;
      } else {
        return num;
      }
    }
  },
  data: function data() {
    return {
      srcImg1: require("@/assets/iconI_06.png"),
      srcImg2: require("@/assets/iconI_06.png"),
      srcImg3: require("@/assets/ii_03.png"),
      srcImg4: require("@/assets/iconI_06.png"),
      srcImg5: require("@/assets/iconI_06.png"),
      srcImg6: require("@/assets/iconI_06.png"),
      srcImg7: require("@/assets/ii_03.png"),
      days: Array(7),
      money: 2,
      singSuccess: false,
      day: 0,
      list: [],
      listMsg: {
        ContinuousSignDays: 0
      },
      bgColor1: "#e4e4e4",
      bgColor2: "#e4e4e4",
      bgColor3: "#e4e4e4",
      bgColor4: "#e4e4e4",
      bgColor5: "#e4e4e4",
      bgColor6: "#e4e4e4",
      bgColor7: "#e4e4e4",
      num: 0,
      feng: this.showModal,
      isOpenAd: false,
      adTypeId: 0,
      AdObj: {}
    };
  },
  methods: {
    hide: function hide() {
      var _this = this;

      // 彈出廣告
      this.adTypeId = 3;
      var data = {
        flowAdTypeId: this.adTypeId
      };
      apiGetFlowAdByType(data, true).then(function (res) {
        if (res.Data.Id == 0) {
          _this.isOpenAd = false;
        } else {
          _this.isOpenAd = true;
          _this.AdObj = res.Data;
        }
      });
      var self = this;
      this.$emit("update:showModal", false);
      this.feng = false;
    },
    getDayDate: function getDayDate() {
      var _this2 = this;

      var data = {};
      var self = this;
      apiSign(data, true).then(function (res) {
        if (res.code == 200) {
          self.feng = true;

          _this2.$emit("update:showModal", true);

          Toast({
            message: res.message,
            duration: 1000
          });
          _this2.listMsg.ContinuousSignDays = res.data.ContinuousSignDays;

          if (_this2.listMsg.ContinuousSignDays == 1) {
            _this2.getNum1();
          }

          if (_this2.listMsg.ContinuousSignDays == 2) {
            _this2.getNum1();

            _this2.getNum2();
          }

          if (_this2.listMsg.ContinuousSignDays == 3) {
            _this2.getNum1();

            _this2.getNum2();

            _this2.getNum3();
          }

          if (_this2.listMsg.ContinuousSignDays == 4) {
            _this2.getNum1();

            _this2.getNum2();

            _this2.getNum3();

            _this2.getNum4();
          }

          if (_this2.listMsg.ContinuousSignDays == 5) {
            _this2.getNum1();

            _this2.getNum2();

            _this2.getNum3();

            _this2.getNum4();

            _this2.getNum5();
          }

          if (_this2.listMsg.ContinuousSignDays == 6) {
            _this2.getNum1();

            _this2.getNum2();

            _this2.getNum3();

            _this2.getNum4();

            _this2.getNum5();

            _this2.getNum6();
          }

          if (_this2.listMsg.ContinuousSignDays == 7) {
            _this2.getNum1();

            _this2.getNum2();

            _this2.getNum3();

            _this2.getNum4();

            _this2.getNum5();

            _this2.getNum6();

            _this2.getNum7();
          }
        } else {
          _this2.listMsg.ContinuousSignDays = res.data.ContinuousSignDays;

          if (_this2.listMsg.ContinuousSignDays == 1) {
            _this2.getNum1();
          }

          if (_this2.listMsg.ContinuousSignDays == 2) {
            _this2.getNum1();

            _this2.getNum2();
          }

          if (_this2.listMsg.ContinuousSignDays == 3) {
            _this2.getNum1();

            _this2.getNum2();

            _this2.getNum3();
          }

          if (_this2.listMsg.ContinuousSignDays == 4) {
            _this2.getNum1();

            _this2.getNum2();

            _this2.getNum3();

            _this2.getNum4();
          }

          if (_this2.listMsg.ContinuousSignDays == 5) {
            _this2.getNum1();

            _this2.getNum2();

            _this2.getNum3();

            _this2.getNum4();

            _this2.getNum5();
          }

          if (_this2.listMsg.ContinuousSignDays == 6) {
            _this2.getNum1();

            _this2.getNum2();

            _this2.getNum3();

            _this2.getNum4();

            _this2.getNum5();

            _this2.getNum6();
          }

          if (_this2.listMsg.ContinuousSignDays == 7) {
            _this2.getNum1();

            _this2.getNum2();

            _this2.getNum3();

            _this2.getNum4();

            _this2.getNum5();

            _this2.getNum6();

            _this2.getNum7();
          }
        }
      });
    },
    getNum1: function getNum1() {
      this.num = 1;
      this.bgColor1 = "#feb700";
      this.srcImg1 = require("@/assets/succ.png");
    },
    getNum2: function getNum2() {
      this.num = 2;
      this.bgColor2 = "#feb700";
      this.srcImg2 = require("@/assets/succ.png");
    },
    getNum3: function getNum3() {
      this.num = 3;
      this.bgColor3 = "#feb700";
      this.srcImg3 = require("@/assets/li.png");
    },
    getNum4: function getNum4() {
      this.num = 4;
      this.bgColor4 = "#feb700";
      this.srcImg4 = require("@/assets/succ.png");
    },
    getNum5: function getNum5() {
      this.num = 5;
      this.bgColor5 = "#feb700";
      this.srcImg5 = require("@/assets/succ.png");
    },
    getNum6: function getNum6() {
      this.num = 6;
      this.bgColor6 = "#feb700";
      this.srcImg6 = require("@/assets/succ.png");
    },
    getNum7: function getNum7() {
      this.num = 7;
      this.bgColor7 = "#feb700";
      this.srcImg7 = require("@/assets/li.png");
    },
    question: function question() {}
  },
  created: function created() {
    var token = localStorage.getItem("token");

    if (token) {
      this.getDayDate();
    }
  }
};
