import Vue from "vue";
import { Toast } from "vant";
Vue.use(Toast);
import { apiGetContactUsInfo } from "@/api/Appsetting.js";
import { apiGetMemberMatchAndUserRank } from "@/api/match.js";
import back from "@/components/Botton/back.vue";
export default {
  name: "history",
  components: {
    back: back
  },
  filters: {
    state: function state(val) {
      if (val = "已结算") {
        return 1;
      } else {
        return 2;
      }
    }
  },
  data: function data() {
    return _defineProperty({
      list: [this.$t('Ordinary'), this.$t('Rank'), this.$t('Challenge')],
      num: 0,
      imgSrc: "",
      showImg: false,
      nomsg: true,
      noMore: false,
      noMore1: false,
      noMore2: false,
      // load: false,
      usualList: [],
      //普通赛列表1
      rankList: [],
      //排位赛列表2
      challageList: [],
      //挑战赛列表3
      letfTeam: "",
      battle: "",
      rightTeam: "",
      isLoading: false,
      //下拉
      loading: false,
      //上拉
      finished: false,
      //是否已加载完所有数据
      indexNum: 1,
      show: true,
      states: true,
      states2: false,
      IndexNum: 1,
      busy: false,
      typeNum: 1,
      typeNum2: 1,
      lang: "",
      totalPages: "",
      immediate: true,
      noData: false,
      usualListLoading: false,
      rankListLoading: false
    }, "challageList", false);
  },
  methods: {
    goBack: function goBack() {
      this.$router.go(-1);
    },
    getHistoryList: function getHistoryList() {
      var _this = this;

      this.busy = true;
      var data = {
        index: this.IndexNum,
        count: 10,
        type: this.typeNum //普通赛

      };
      this.loading = true;
      this.noData = false;
      apiGetMemberMatchAndUserRank(data, true).then(function (res) {
        if (Math.abs(res.Code) >= 600 || res.Data.length <= 0) {
          _this.loading = false;
          _this.noData = true;
        } else {
          _this.busy = false;
          _this.totalPages = res.TotalPages;

          if (_this.typeNum == 1) {
            _this.usualList = res.Data;
          }

          if (_this.typeNum == 2) {
            _this.rankList = res.Data;
          }

          if (_this.typeNum == 3) {
            _this.challageList = res.Data;
          }

          _this.loading = false;
          _this.noData = false;
        }
      });
    },
    getNum: function getNum(index) {
      this.num = index;

      if (this.num == 0) {
        this.typeNum = 1;
        this.IndexNum = 1;
        this.usualList = [];
        this.getHistoryList();
      }

      if (index == 1) {
        this.typeNum = 2;
        this.IndexNum = 1;
        this.rankList = [];
        this.getHistoryList();
      }

      if (index == 2) {
        this.typeNum = 3;
        this.IndexNum = 1;
        this.challageList = [];
        this.getHistoryList();
      }
    },
    load: function load() {
      var _this2 = this;

      if (this.IndexNum >= this.totalPages) {
        this.loading = false;
        this.busy = true;
        return;
      }

      this.busy = true;
      this.IndexNum++;
      var data = {
        index: this.IndexNum,
        count: 10,
        type: this.typeNum
      };

      if (data.type == 1) {
        this.loading = true;
        this.noData = false;
        apiGetMemberMatchAndUserRank(data, true).then(function (res) {
          for (var i = 0; i < res.Data.length; i++) {
            if (res.Data.length !== 0) {
              _this2.usualList.push(res.Data[i]);
            }
          }

          _this2.busy = false;
          _this2.loading = false;
        });
      } else if (data.type == 2) {
        this.loading = true;
        apiGetMemberMatchAndUserRank(data, true).then(function (res) {
          for (var i = 0; i < res.Data.length; i++) {
            if (res.Data.length !== 0) {
              _this2.rankList.push(res.Data[i]);
            }
          }

          _this2.busy = false;
          _this2.loading = false;
        });
      } else if (data.type == 3) {
        this.loading = true;
        apiGetMemberMatchAndUserRank(data, true).then(function (res) {
          for (var i = 0; i < res.Data.length; i++) {
            if (res.Data.length !== 0) {
              _this2.challageList.push(res.Data[i]);
            }
          }

          _this2.busy = false;
          _this2.loading = false;
        });
      }
    }
  },
  created: function created() {
    this.lang = JSON.parse(localStorage.getItem("lang")).tag;
    this.getHistoryList();
  }
};
