import { apiGetNewsList, apiReadNewsRecord } from "@/api/News.js";
import back from "@/components/Botton/back.vue";
import Vue from "vue";
export default {
  name: "News",
  components: {
    back: back
  },
  data: function data() {
    return {
      newsList: [],
      Wid: "2.08rem",
      Hei: "1.64rem",
      // v-infinite-scroll 套件
      busy: false,
      push: false,
      IndexNum: 1,
      noMore: false
    };
  },
  methods: {
    goBack: function goBack() {
      this.$router.go(-1);
    },
    goNewsDetail: function goNewsDetail(id) {
      var data = {};
      apiReadNewsRecord(data, true).then(function (res) {});
      this.$router.push({
        path: "./information/".concat(id)
      });
    },
    getNewsList: function getNewsList() {
      var _this = this;

      var data = {};
      apiGetNewsList(data, true).then(function (res) {
        _this.newsList = res;
      }); //actions的提取方法
      //    this.$store.dispatch('asyncGetNewsList');
    },
    // v-infinite-scroll 套件滑到底加載
    load: function load() {
      var _this2 = this;

      this.busy = true;
      var data = {
        index: this.IndexNum,
        count: 10
      };
      apiGetNewsList(data, true).then(function (res) {
        if (res.news.length == 0) {
          _this2.busy = true;
          _this2.push = false; // this.noMore = true
        } else if (res.news.length > 0) {
          for (var i = 0; i < res.news.length; i++) {
            _this2.newsList.push(res.news[i]);

            _this2.busy = false;
          }

          _this2.IndexNum++;
        }
      });
    }
  },
  computed: {},
  mounted: function mounted() {// const { $route: { params: { id } } } = this;
    // this.getNewsList()
  }
};
