import back from "@/components/Botton/back.vue";
import model from "@/components/model";
import { apiGetClubs, apiGetMemClubs, apiJoinClub } from "@/api/Club.js";
export default {
  components: {
    back: back,
    model: model
  },
  data: function data() {
    return {
      PopularList: [],
      RecommendList: [],
      isMember: 0,
      // 防止连点
      isDisable: false,
      noMission: false,
      popularListNoData: false,
      isOpen: false,
      isCallBack: false,
      callBackMsg: ""
    };
  },
  created: function created() {
    // 判斷是否有會員 1 = 有  2 = 無
    this.isMember = localStorage.getItem("isMember");
    this.getClubList();
  },
  methods: {
    toCreatRule: function toCreatRule() {
      this.$router.push("/CreateClubRule");
    },
    // 獲取俱樂部列表
    getClubList: function getClubList() {
      var _this = this;

      var data = {
        ClubId: 0
      };
      apiGetClubs(data, true).then(function (res) {
        // 獲取推薦俱樂部列表
        _this.RecommendList = res.Data.RecommendList; // 獲取熱門俱樂部列表

        _this.PopularList = res.Data.PopularList;
        _this.RecommendList.length > 0 ? _this.noMission = false : _this.noMission = true;
        _this.PopularList.length > 0 ? _this.popularListNoData = false : _this.popularListNoData = true;
      });
    },
    // 選取俱樂部
    selectClub: function selectClub(id) {
      localStorage.setItem("selectClub", id);
      this.$router.push("/clubData");
    },
    // 申請加入俱樂部
    joinClub: function joinClub() {
      var _this2 = this;

      var data = {
        ClubId: this.clickClubId,
        UName: this.uname,
        Type: 0 // 0 申请进入  1 退出  2 被踢

      };
      apiJoinClub(data, true).then(function (res) {
        _this2.isCallBack = true;
        _this2.callBackMsg = res.Message;
        _this2.isOpen = false;
      });
    }
  }
};
