import back from "@/components/Botton/back.vue";
import { apiGetRankingMedal } from "@/api/Rankboard";
export default {
  components: {
    back: back
  },
  data: function data() {
    return {
      category: [this.$t('BetMedal'), this.$t('MedalVictory'), this.$t('MedalWinningCoins')],
      // 【 投注勋章 】【 胜次勋章 】【 赢币勋章 】
      categoryType: 0,
      imgAry: [],
      imgData: [],
      ShowExplain: false
    };
  },
  created: function created() {
    // 類別選擇- 呼叫API
    this.getMedalRank(this.categoryType); //類別選擇-切換畫面顯示

    this.changeCategory(this.categoryType);
  },
  methods: {
    //類別選擇-切換畫面顯示
    changeCategory: function changeCategory(index) {
      this.categoryType = index;
    },
    // 類別選擇- 呼叫API
    getMedalRank: function getMedalRank(index) {
      var _this = this;

      this.imgAry = [];
      var data = {
        type: index
      };
      apiGetRankingMedal(data, true).then(function (res) {
        _this.imgData = res; // 把陣列變成兩個一包

        for (var i = 0; i < _this.imgData.length; i += 2) {
          _this.imgAry.push(_this.imgData.slice(i, i + 2));
        }
      });
    },
    // 彈窗
    showPopup: function showPopup() {
      this.ShowExplain = true;
    }
  }
};
