import { apiChangePwd } from "@/api/User.js";
import back from "@/components/Botton/back.vue";
import Vue from "vue";
import { Toast } from "vant";
Vue.use(Toast);
export default {
  inject: ["reload"],
  //注入reload方法
  components: {
    back: back
  },
  data: function data() {
    return {
      pass1: "",
      pass2: "",
      pass3: "",
      iconPass: require("@/assets/icons/hide1.png"),
      really: true,
      really2: true,
      really1: true
    };
  },
  computed: {
    types: function types() {
      return this.really ? "password" : "text";
    },
    types2: function types2() {
      return this.really2 ? "password" : "text";
    },
    types1: function types1() {
      return this.really1 ? "password" : "text";
    }
  },
  methods: {
    goBack: function goBack() {
      this.$router.go(-1);
    },
    saveUp: function saveUp() {
      var _this = this;

      var data = {
        OldPwd: this.pass1,
        NewPwd: this.pass2,
        ConfirmNewPwd: this.pass3
      };

      if (data.OldPwd == "" || data.NewPwd == "" || data.ConfirmNewPwd == "") {
        // return this.$Alert.showWarning.call(this, this.$t("PasswordNotFilled"))
        return this.Alert.showMsg(this.$t("PasswordNotFilled"), "warning", 1200, true, true);
      }

      apiChangePwd(data, true).then(function (res) {
        if (Math.abs(res.Code) >= 600) {
          var m = _this.$message({
            message: res.message,
            type: "error",
            center: true,
            duration: 1000
          });

          setTimeout(function () {
            return m.close();
          }, 1200);
        } else {
          if (res.IsSuccess) {
            localStorage.removeItem("token");

            var _m = _this.$message({
              message: res.message,
              type: "success",
              center: true,
              duration: 1000
            });

            setTimeout(function () {
              return _m.close();
            }, 1200);
            setTimeout(function () {
              _this.$router.push("/");
            }, 1500);
          }
        }
      });
    }
  }
};
