import back from "@/components/Botton/back.vue";
import { apiGetClubGamblingBetResultList, apiGetClubGamblingBetResult } from "@/api/ClubGamblings.js";
export default {
  components: {
    back: back
  },
  data: function data() {
    return {
      lang: "",
      resultData: [],
      betId: "",
      isLoading: false,
      isNoData: false
    };
  },
  created: function created() {
    this.lang = JSON.parse(localStorage.getItem("lang")).tag;

    if (localStorage.getItem("singleResult") == undefined || localStorage.getItem("singleResult") == null || localStorage.getItem("singleResult") == "false") {
      this.getBetResult();
    } else {
      this.betId = localStorage.getItem("betId");
      this.singleBetResult();
    }
  },
  methods: {
    getBetResult: function getBetResult() {
      var _this = this;

      // 所有賽果列表()
      this.isLoading = true;
      apiGetClubGamblingBetResultList("", true).then(function (res) {
        if (Math.abs(res.Code) >= 600) {
          var vm = _this;
          var m = vm.$message({
            message: res.Message,
            duration: 3000,
            type: "error",
            center: true
          });
          setTimeout(function () {
            m.close();

            _this.$router.push("/myClub");
          }, 2000);
          return;
        } else if (res.Data.length == 0) {
          _this.isLoading = false;
          _this.isNoData = true;
        } else {
          _this.isLoading = false;
          _this.isNoData = false;
          _this.resultData = res.Data;
        }
      });
    },
    // 單一點擊查看
    singleBetResult: function singleBetResult() {
      var _this2 = this;

      var data = {
        clubGamblingId: this.betId
      };
      this.isLoading = true;
      apiGetClubGamblingBetResult(data, true).then(function (res) {
        if (Math.abs(res.Code) >= 600) {
          var vm = _this2;
          var m = vm.$message({
            message: res.Message,
            duration: 3000,
            type: "error",
            center: true
          });
          setTimeout(function () {
            m.close();

            _this2.$router.push("/myClub");
          }, 2000);
          return;
        } else if (res.Data.length == 0) {
          _this2.isLoading = false;
          _this2.isNoData = true;
        } else {
          _this2.isLoading = false;
          _this2.isNoData = false;
          _this2.resultData = res.Data;
        }
      });
    }
  },
  destroyed: function destroyed() {
    localStorage.setItem("singleResult", false);
  }
};
