import back from "@/components/Botton/back.vue";
export default {
  components: {
    back: back
  },
  data: function data() {
    return {
      list: {
        title: "大将：上赛季的争冠经验帮助利物浦成长",
        time: "2019-09-22"
      },
      lice: false,
      t: ""
    };
  },
  methods: {
    like: function like() {
      var _this = this;

      clearInterval(this.t);
      this.t = setInterval(function () {
        if (_this.$refs.cont.scrollTop >= 200) {
          _this.$refs.cont.scrollTop -= _this.$refs.cont.scrollTop / 10;
        } else if (_this.$refs.cont.scrollTop >= 20) {
          _this.$refs.cont.scrollTop = 0;
          clearInterval(_this.t);
        }
      }, 1);
    },
    scrollTop: function scrollTop() {
      // 判断回到顶部的元素是否显示
      var lik = this.$refs.cont.scrollTop;

      if (lik >= 300) {
        this.lice = true;
      } else {
        this.lice = false;
      }
    },
    goBack: function goBack() {
      this.$router.go(-1);
    },
    Tocontone: function Tocontone() {
      document.querySelector("#contone").scrollIntoView(true);
    },
    Toconttwo: function Toconttwo() {
      document.querySelector("#conttwo").scrollIntoView(true);
    },
    Tocontthree: function Tocontthree() {
      document.querySelector("#contthree").scrollIntoView(true);
    },
    Tocontfour: function Tocontfour() {
      document.querySelector("#contfour").scrollIntoView(true);
    },
    Tocontfive: function Tocontfive() {
      document.querySelector("#contfive").scrollIntoView(true);
    },
    Tocontsix: function Tocontsix() {
      document.querySelector("#contsix").scrollIntoView(true);
    }
  },
  mounted: function mounted() {
    var id = this.$route.params.id;
    this.$refs.cont.addEventListener("scroll", this.scrollTop); // 绑定scroll事件
  }
};
