import inputComp from "@/components/Index/Input.vue";
import { apiLogin } from "@/api/User";
import { apiGetMemClubs } from "@/api/Club.js";
export default {
  components: {
    inputComp: inputComp
  },
  computed: {
    ifPassword: function ifPassword() {
      return this.togglePassword ? "password" : "text";
    }
  },
  created: function created() {
    if (this.$route.query.token) {
      localStorage.setItem("token", this.$route.query.token);
    }

    var token = localStorage.getItem("token");

    if (token) {
      this.$router.push("/");
    }
  },
  data: function data() {
    return {
      account: "",
      password: "",
      imgCode: "",
      togglePassword: true
    };
  },
  methods: {
    gojump: function gojump() {
      this.$router.push("/");
    },
    gofp: function gofp() {
      this.$router.push("/ForgetPassword");
    },
    gorg: function gorg() {
      this.$router.push("/register");
    },
    getAccount: function getAccount(res) {
      this.account = res;
    },
    getPassword: function getPassword(res) {
      this.password = res;
    },
    // login () {
    //   let data = {
    //     Name: this.account,
    //     Password: this.password
    //   };
    //   this.$store.dispatch("asyncLogin", data);
    //   // let result = this.$store.state.user.code;
    //   // if (result === 200) {
    //   //   this.$toast.success("登录成功");
    //   //   setTimeout(() => {
    //   //     this.$router.push("/home");
    //   //   });
    //   // }
    // }
    login: function login(v) {
      var _this = this;

      var data = {
        Name: this.account,
        Password: this.password
      };

      if (this.account == "") {
        var m = this.$message({
          message: this.$t("AccountEmpty"),
          // 【 帐号不能为空 】
          duration: 1000,
          type: "error",
          center: true
        });
        setTimeout(function () {
          return m.close();
        }, 1200);
        return;
      }

      ;

      if (this.password == "") {
        var _m = this.$message({
          message: this.$t("PasswordBlank"),
          // 【 密码不能为空 】
          duration: 1000,
          type: "error",
          center: true
        });

        setTimeout(function () {
          return _m.close();
        }, 1200);
        return;
      }

      ;
      var vm = this;
      apiLogin(data).then(function (res) {
        if (res.code === 200) {
          localStorage.setItem("token", res.AccessToken);
          localStorage.setItem("UId", res.UId);
          localStorage.setItem("RefreshToken", res.RefreshToken);
          localStorage.setItem("logintime", new Date().getTime());
          localStorage.setItem("uname", vm.account);

          var _m2 = vm.$message({
            message: _this.$t('LoginSuccessful'),
            // 【 登录成功 】
            duration: 1000,
            type: "success",
            center: true
          });

          vm.$parent.isOn = true;
          localStorage.setItem("isOn", true);
          vm.$parent.handlePlay();
          vm.$router.push("/"); // 獲取俱樂部會員資格與否 

          _this.isMember();
        } else if (Math.abs(res.Code) >= 600) {
          var _m3 = vm.$message({
            message: res.Message,
            duration: 3000,
            type: "error",
            center: true
          });

          setTimeout(function () {
            return _m3.close();
          }, 1200);
        }
      }); // .then(()=> {
      //   // 獲取俱樂部會員資格與否 
      //   this.isMember();
      // });
    },
    // 語系選擇
    langSelect: function langSelect(name, tag) {
      localStorage.setItem("lang", JSON.stringify({
        name: name,
        tag: tag
      }));
      location.reload();
    },
    // 獲取俱樂部會員資格與否   1 = 有資格;  2 = 無資格
    isMember: function isMember() {
      // let userName = localStorage.getItem('uname')
      // let data = {}
      apiGetMemClubs("", true).then(function (res) {
        if (res.Data == null) {
          // 無會員
          localStorage.setItem("isMember", 2);
        } else {
          // 有會員
          localStorage.setItem("isMember", 1);
          localStorage.setItem("ClubId", res.Data.ClubId); // 判斷是否為管理階級

          if (res.Data.Name == "会长") {
            localStorage.setItem("isManagement", 1);
          } else if (res.Data.Name == "副会长") {
            localStorage.setItem("isManagement", 2);
          } else if (res.Data.Name == "会员") {
            localStorage.setItem("isManagement", 3);
          }
        }
      });
    }
  },
  mounted: function mounted() {
    this.account = localStorage.getItem("uname");
  }
};
