import { apiGetMatchResultByMatchId } from "@/api/match.js";
import back from "../../components/Botton/back.vue";
export default {
  components: {
    back: back
  },
  name: "gameRes",
  created: function created() {
    this.getMatch();
  },
  data: function data() {
    return {
      Pindex: 0,
      num: 0,
      HalfNumLeft: 0,
      AllNumLeft: 0,
      HalfNumRight: 0,
      AllNumRight: 0,
      startTime: this.yesterdayDate(),
      endTime: this.todayDate(),
      items: false,
      matchs: true,
      MatchItems: "",
      MatchName: "",
      match: ""
    };
  },
  methods: {
    todayDate: function todayDate() {
      //今天的时间
      var day = new Date();
      var s1 = day.getFullYear() + "-" + (day.getMonth() + 1) + "-" + day.getDate();
      return s1;
    },
    yesterdayDate: function yesterdayDate() {
      //昨天的时间
      var day = new Date();
      var s1 = day.getFullYear() + "-" + (day.getMonth() + 1) + "-" + (day.getDate() - 7);
      return s1;
    },
    getMatch: function getMatch() {
      var _this = this;

      this.match = this.$route.params.name;
      var data = {
        matchId: this.$route.params.id,
        sDate: this.startTime,
        eDate: this.endTime,
        // matchId: 7218,
        // sDate: "2019-10-11",
        // eDate: "2019-10-14",
        index: 1,
        count: 10
      };
      apiGetMatchResultByMatchId(data).then(function (res) {
        _this.MatchItems = res.Data;
      });
    },
    //赛事种类
    goBack: function goBack() {
      this.$router.go(-1);
    },
    //篮球
    pickTime: function pickTime() {
      var _this2 = this;

      var data = {
        matchId: this.$route.params.id,
        sDate: this.startTime,
        eDate: this.endTime,
        // matchId: 7218,
        // sDate: "2019-10-11",
        // eDate: "2019-10-14",
        index: 1,
        count: 20
      };
      apiGetMatchResultByMatchId(data).then(function (res) {
        _this2.MatchItems = res.Data;
      });
    },
    //赛事结果
    showitems: function showitems(id, name) {
      var _this3 = this;

      this.$router.push("/game");
      this.matchs = false;
      this.MatchName = name;
      var data = {
        matchId: id,
        sDate: this.startTime,
        eDate: this.endTime,
        // matchId: 7218,
        // sDate: "2019-10-11",
        // eDate: "2019-10-14",
        index: 1,
        count: 10
      };
      this.MatchItems = [1];
      apiGetMatchResultByMatchId(data).then(function (res) {
        if (res.Data.length > 0) {
          _this3.items = true;
        }

        _this3.MatchItems = res.Data;
      });
    }
  },
  watch: {
    endTime: function endTime(newVal, oldVal) {}
  }
};
