import { apiGetRecommendMatchItem, apiGetBetResponse, apiBetRecommend, apiIsMaintenance } from "@/api/match.js";
import Tinder from "vue-tinder";
import Vue from "vue";
import { Dialog } from "vant";
import { Image } from "vant";
import { randomBytes } from "crypto";
import { betSuccess, betPass } from "@/utils/medias";
import { apiGetUserInfo } from "@/api/User.js";
Vue.use(Dialog).use(Image);
export default {
  name: "homeCard",
  components: {
    Tinder: Tinder
  },
  data: function data() {
    return {
      isMaintenance: false,
      noData: true,
      isSwipe: true,
      isLoading: false,
      tan: false,
      games: [],
      //比赛集
      matchList: [],
      previousGame: null,
      playIdIndex: 0,
      //当前盘口位置
      pageIndex: 1,
      pageSize: 15,
      blank: false
    };
  },
  filters: {
    host: function host(val) {
      if (val) {
        return "主";
      } else if (!val) {
        return "客";
      }
    }
  },
  computed: {},
  created: function created() {
    this.getMatchList();
    this.getMsg();
  },
  methods: {
    getMsg: function getMsg() {
      var data = {};
      apiGetUserInfo(data, true).then(function (res) {
        localStorage.setItem("RankId", res.RankId);
        localStorage.setItem("nickName", res.NickName);
        localStorage.setItem("UImg", res.UImg);
      });
    },
    // gomatch() {
    //   this.$router.push("/gameSport");
    // },
    onSubmit: function onSubmit(choice) {
      // choice.type： 结果，'like'：右滑, 'nope'：左滑, 'super'：上滑
      // choice.key：  被移除卡片的 keyName
      // choice.item： queue 中的子对象
      this.previousGame = choice.item;

      if (choice.type == "nope") {
        // 左滑音效
        betPass.play();
      } else if (choice.type == "like") {
        this.que(); // 右滑音效

        betSuccess.play();
      }

      if (this.$refs.tinder.queue.length == 0) {
        this.getMatchList();
      }
    },
    goGames: function goGames() {
      this.$router.push("/gamesport");
    },
    getMatchList: function getMatchList() {
      var _this = this;

      var data = {
        index: this.pageIndex,
        count: this.pageSize
      };
      this.isLoading = !this.isLoading;
      apiGetRecommendMatchItem(data).then(function (res) {
        _this.pageIndex++;
        _this.isLoading = !_this.isLoading;

        if (res.Data.length == 0) {
          _this.noData = true; //维护检查

          apiIsMaintenance().then(function (res) {
            _this.isMaintenance = res.IsMaintenance;
          });
        } else {
          _this.games = res.Data;

          _this.randomGames();

          _this.noData = false;
        }

        if (_this.pageIndex > res.TotalPages) {
          _this.pageIndex = 1;
        }

        _this.matchList = res.Data;

        if (_this.matchList.length == 0) {
          setTimeout(function () {
            _this.blank = true;
          }, 500);
        } else {
          _this.blank = false;
        }
      });
    },
    quxiao: function quxiao() {
      this.tan = false;
      this.$refs["tinder"].decide([this.previousGame]);
    },
    que: function que() {
      var _this2 = this;

      var data = {
        playId: this.previousGame.PlayId,
        betIntegral: this.previousGame.MinBetAmount
      };
      apiBetRecommend(data, true).then(function (res) {
        if (res.IsBetSucceed) {
          var m = _this2.$message({
            message: _this2.$t("BettingSuccessfulPrompt") + " " + res.AvailableIntegral,
            type: "success",
            duration: 1500
          });

          setTimeout(function () {
            return m.close();
          }, 1200);
        } else if (res.Code == 600) {
          var _m = _this2.$message({
            message: res.Message,
            type: "error",
            duration: 1000
          });

          setTimeout(function () {
            return _m.close();
          }, 1200);

          _this2.$refs["tinder"].decide([_this2.previousGame]);
        }
      });
      this.tan = false;
    },
    // 隨機組合
    randomGames: function randomGames() {
      if (this.games) {
        this.games.sort(function () {
          return 0.5 - Math.random();
        });
      }
    }
  }
};
