import { apiAddFeedback } from '@/api/FeedB.js';
import Vue from 'vue';
import { Toast, Notify } from 'vant';
Vue.use(Toast).use(Notify);
export default {
  data: function data() {
    return {
      textarea: ''
    };
  },
  methods: {
    goBack: function goBack() {
      this.$router.go(-1);
    },
    sure: function sure() {
      var _this = this;

      var data = {
        // FeedbackId: null,
        FeedbackTypeId: null,
        FeedbackContent: this.textarea,
        CreationTime: new Date(),
        LastModifyTime: "2019-10-14T08:05:22.363Z"
      };
      apiAddFeedback(data, true).then(function (res) {
        if (_this.textarea === '') {
          _this.$message({
            message: '反馈内容不能为空',
            type: 'success',
            center: true,
            duration: 800
          });
        } else if (res.IsSuccess) {
          Toast.success({
            message: '反馈提交成功',
            duration: 1000
          });
          _this.textarea = ''; // ，我会回尽快给您反馈
        } else {
          Toast.fail({
            message: '意见提交失败',
            duration: 1000
          });
        }
      });
    }
  }
};
