import back from "../../components/Botton/back.vue";
import { apiGetChallengeMatchByRandom, apiBet } from "@/api/match";
import { betSuccess } from "@/utils/medias";
export default {
  components: {
    back: back
  },
  created: function created() {
    this.getMatch();
  },
  data: function data() {
    return {
      num: -1,
      num2: -1,
      matchs: "",
      bettype: "",
      bettype2: "",
      playid: 0,
      playid2: 0,
      bet3: "",
      bet2: "",
      Reward: ""
    };
  },
  methods: {
    goBet: function goBet() {
      var _this = this;

      if (this.playid == 0) {
        alert(this.$t('PleaseSelectTheFirstMatch'));
        return;
      }

      if (this.playid2 == 0) {
        alert(this.$t('PleaseSelectTheSecondMatch'));
        return;
      } //第一次投注


      var data = {
        playId: this.playid,
        betType: 3
      }; //第二次投注

      var data1 = {
        playId: this.playid2,
        betType: 3
      };
      apiBet(data, true).then(function (res) {
        // this.bet3 = res.IsBetSucceed;
        if (res.IsBetSucceed) {
          _this.$message({
            message: _this.$t('BettingSuccess'),
            duration: 2500,
            center: true,
            type: "success"
          }); // 下注成功音效


          betSuccess.play();
          setTimeout(function () {
            _this.$router.push("/challengehome");
          }, 1000);
        }
      });
      apiBet(data1, true).then(function (res) {
        // this.bet2 = res.IsBetSucceed;
        if (Math.abs(res.Code) >= 600) {
          _this.$message({
            message: res.Message,
            duration: 3000,
            type: "error",
            center: true
          });
        }
      }); // this.$nextTick(() => {
      //   if (this.bet1 == true && this.bet2 == true) {
      //     this.$toast.success({
      //       message: "下注成功",
      //       duration: 1000
      //     });
      //   }
      // });
    },
    bet: function bet(id, playid) {
      this.num = id;
      this.playid = playid;
    },
    bet1: function bet1(id, playid) {
      this.num2 = id;
      this.playid2 = playid;
    },
    getMatch: function getMatch() {
      var _this2 = this;

      apiGetChallengeMatchByRandom("", true).then(function (res) {
        _this2.Reward = res.Reward;

        if (res.data.Data.length === 0) {
          _this2.$message({
            message: _this2.$t('NoGameTip'),
            duration: 3000,
            type: "error",
            center: true
          });
        } else {
          _this2.matchs = res.data.Data;
        } // res.data.Data[0].CommonPlayViewModelGroups[0].shift();
        // res.data.Data[1].CommonPlayViewModelGroups[0].shift();
        // this.bettype = res.data.Data[0].CommonPlayViewModelGroups[0];
        // this.bettype2 = res.data.Data[1].CommonPlayViewModelGroups[0];


        _this2.bettype = res.data.Data[0].CommonPlayViewModelGroups[0].filter(function (item) {
          return item.PlayId != -1;
        });
        _this2.bettype2 = res.data.Data[1].CommonPlayViewModelGroups[0].filter(function (item) {
          return item.PlayId != -1;
        });
      });
    }
  }
};
