      this.num = 0;
      this.$router.push("/home");
    },
    goClub: function goClub() {
      var _this = this;

      // this.num = -1;
      // this.show = !this.show;
      apiGetMemClubs("", true).then(function (res) {
        if (res.Data == null) {
          // 無會員
          localStorage.setItem("isMember", 2);

          _this.$router.push("/CreateClub");
        } else {
          // 有會員
          localStorage.setItem("isMember", 1);
          localStorage.setItem("ClubId", res.Data.ClubId); // 判斷是否為管理階級

          if (res.Data.Name == "会长") {
            localStorage.setItem("isManagement", 1);
          } else if (res.Data.Name == "副会长") {
            localStorage.setItem("isManagement", 2);
          } else if (res.Data.Name == "会员") {
            localStorage.setItem("isManagement", 3);
          }

          _this.$router.push("/myClub");
        }
      });
    },
    goRankBoard: function goRankBoard() {
      this.$router.push("/rankboard");
    },
    changeNum: function changeNum(id) {
      this.show = false;
      this.num = id;
      this.$message({
        message: this.$t("NotYetOpen"),
        type: "warning",
        center: true,
        duration: 1200
      });
    },
    change: function change(id) {
      this.show = false;
      this.num = 1;
    },
