import { apiGetUserAddress, apiAddEditMemAddress } from '@/api/User.js';
import back from "@/components/Botton/back.vue";
export default {
  //从Detail返回List时，修改List的keepAlive为true,确保返回List页面时使用缓存不刷新页面
  beforeRouteLeave: function beforeRouteLeave(to, from, next) {
    if (to.name === 'shoplists') {
      to.meta.keepAlive = true;
    }

    next();
  },
  components: {
    back: back
  },
  data: function data() {
    return {
      ren: false,
      noMsg: '',
      addList: []
    };
  },
  methods: {
    goHome: function goHome() {
      this.$router.push('/shop');
    },
    goEdit: function goEdit(id, name, phone, address, moren, timer) {
      var arrs = [];
      arrs.push(id, name, phone, address, moren, timer);
      this.$router.push({
        path: '/edit',
        query: {
          arrs: arrs
        }
      });
    },
    goAdd: function goAdd() {
      this.$router.push('/addList');
    },
    goShop: function goShop(id, name, phone, address, moren, timer) {
      var shops = [];
      shops.push(id, name, phone, address, moren, timer); // 数组转字符串

      var shopRess = JSON.stringify(shops);
      localStorage.setItem('shops', shopRess);
      this.$router.push({
        path: '/shop',
        name: 'shoplists',
        params: {
          shops: shops
        }
      });
    },
    getAddMsg: function getAddMsg() {
      var _this = this;

      var data = {};
      apiGetUserAddress(data, true).then(function (res) {
        if (Math.abs(res.Code) >= 600) {
          _this.noMsg = true;
        } else {
          _this.addList = res.Data;

          if (_this.addList.length > 0) {
            _this.noMsg = false;
          } else {
            _this.noMsg = true;
          }
        }
      });
    }
  },
  created: function created() {
    this.getAddMsg();
  }
};
