import "core-js/modules/es6.regexp.match";
//listtype请求
import { apiGetListTypeList, apiGetListTypeListAndUserRank, apiGetListByListTypeId } from "@/api/ListType";
import store from "../index";
var ListType = {
  state: {
    rankList: {},
    RankUserlist: [],
    // ListTypeId: [],
    UserRank: "",
    UserIntegral: ""
  },
  mutations: {
    getRankList: function getRankList(state, payload) {
      payload.shift(), payload.pop(), payload.reverse();
      state.rankList = payload;
    },
    upRankUserlist: function upRankUserlist(state, payload) {
      state.RankUserlist = payload;
    },
    upListTypeId: function upListTypeId(state, payload) {
      state.ListTypeId = payload.id;
      state.UserRank = payload.rank;
      state.UserIntegral = payload.int;
    },
    uplistbylist: function uplistbylist(state, payload) {
      state.listbylist = payload;
    }
  },
  actions: {
    asyncGetListByListTypeId: function asyncGetListByListTypeId(context) {
      var data = {
        listTypeId: context.state.ListTypeId,
        index: 1,
        count: 100
      };
      apiGetListByListTypeId(data, true).then(function (res) {
        context.commit("uplistbylist", res.Data);
      });
    },
    //获取排行榜
    asyncGetListType: function asyncGetListType(context) {
      var matchId = {
        MatchId: store.state.match.MatchId,
        index: 1,
        count: 10
      };
      apiGetListTypeList(matchId).then(function (res) {
        context.commit("getRankList", res.Data);
      });
    },
    //获取排行榜今日次日总日
    asyncgetranklist: function asyncgetranklist(context) {
      var data = {
        matchId: store.state.match.MatchId,
        index: 1,
        count: 100
      };
      apiGetListTypeListAndUserRank(data, true).then(function (res) {
        var hashdata = res.Data;
        var zuotian = hashdata[hashdata.length - 1];
        var zong = hashdata[0];
        var today = hashdata[hashdata.length - 2];
        var arr = [zong, today, zuotian];
        context.commit("upRankUserlist", arr);
      });
    }
  }
};
export default ListType;
