import back from "@/components/Botton/back.vue";
import newcityPinyin from '@/utils/cityPinyin.js';
import newCity from '@/utils/city.js';
import { apiAddEditMemAddress } from '@/api/User.js';
import { mapActions, mapGetters, mapMutations } from 'vuex';
import Vue from 'vue';
import { Area, Toast } from 'vant';
Vue.use(Area).use(Toast);
export default {
  components: {
    back: back
  },
  data: function data() {
    return {
      clear: true,
      getPer: '',
      phone: '',
      address: '',
      detailAdd: '',
      value2: true,
      isDefault: 0,
      //省市区
      cityListNew: "",
      cityCodeList: [],
      Max: 4,
      lang: ""
    };
  },
  watch: {},
  created: function created() {
    this.lang = JSON.parse(localStorage.getItem("lang")).tag;
    this.lang == "en-US" ? this.cityListNew = newcityPinyin : this.cityListNew = newCity;
  },
  mounted: function mounted() {// console.log(newCityPinyin)
    // console.log(newCity)
    // console.log(JSON.stringify(newCity))
  },
  methods: {
    oninput: function oninput(val) {},
    goHome: function goHome() {
      this.$router.push('/address');
    },
    //组件确定键
    handleChange: function handleChange() {},
    //将地址code转换为地区字
    easyCity: function easyCity() {
      var _this = this;

      var easyAddress = '';
      this.cityListNew.forEach(function (item) {
        if (item.value == _this.cityCodeList[0]) {
          //label就是字了   cityCodeList=[13,23,55]这个形式的所以要改变
          easyAddress += item.label;
          item.children.forEach(function (jtem) {
            if (jtem.value == _this.cityCodeList[1]) {
              easyAddress += jtem.label;
            }

            jtem.children.forEach(function (ktem) {
              if (ktem.value == _this.cityCodeList[2]) {
                easyAddress += ktem.label;
              }
            });
          });
        }
      });
      this.address = easyAddress;
    },
    goBack: function goBack() {
      this.$router.go(-1);
    },
    changes: function changes() {
      if (this.value2) {
        return 1;
      } else {
        return 0;
      }
    },
    addAddress: function addAddress() {
      var _this2 = this;

      // 判断手机号是否合法
      this.easyCity();
      var isMo = 0;

      if (this.value2) {
        isMo = 1;
      } else {
        isMo = 0;
      }

      var data = {
        AId: '',
        UId: '',
        UName: this.getPer,
        UPhoneNumber: this.phone,
        URedeemAddress: this.address + this.detailAdd,
        IdentityCard: '123123',
        CreateTime: new Date(),
        IsDefault: isMo
      };

      if (data.UName == '' || data.UPhoneNumber == '' || data.URedeemAddress == '') {
        var m = this.$message({
          message: this.$t("CompleteAddressInformation"),
          type: "warning",
          duration: 1000
        });
        setTimeout(function () {
          return m.close();
        }, 1200);
      } else {
        apiAddEditMemAddress(data, true).then(function (res) {
          if (res.Data == 1) {
            var _m = _this2.$message({
              message: _this2.$t("SavedSuccessfully"),
              type: "success",
              duration: 1000
            });

            setTimeout(function () {
              return _m.close();
            }, 1200);

            _this2.$router.replace('/address');

            _this2.$router.go(-1);
          } else {
            var _m2 = _this2.$message({
              message: _this2.$t("successfullyDeleted"),
              type: "error",
              duration: 1000
            });

            setTimeout(function () {
              return _m2.close();
            }, 1200);
          }
        });
      }
    }
  }
};
