import back from "@/components/Botton/back.vue";
import { apiGetClubActivityMatchAndPlay, apiActivityBets } from "@/api/ClubActivitys.js";
export default {
  name: "myClub",
  components: {
    back: back
  },
  data: function data() {
    return {
      matchObj: [],
      matchObjIndex: undefined,
      btnIndex: undefined,
      active: undefined,
      newPlayAry: [],
      clubActivityId: ""
    };
  },
  created: function created() {
    localStorage.setItem("categoryIndex", 1);
    this.getMatch();
  },
  methods: {
    getMatch: function getMatch() {
      var _this = this;

      this.clubActivityId = localStorage.getItem("AcvtiveId");
      var data = {
        ClubActivityId: this.clubActivityId
      };
      apiGetClubActivityMatchAndPlay(data, true).then(function (res) {
        _this.matchObj = res.Data; // 替Plays的每個按鈕添加一個isClick，供點選使用 + 塞一包空陣列進去給副標題使用

        _this.matchObj.forEach(function (el) {
          el.PlayText = "";
          el.Plays.forEach(function (play) {
            play.isClick = 0;
          });
        }); // 副標題


        for (var i = 0; i < _this.matchObj.length; i++) {
          for (var j = 0; j < _this.matchObj[i].Plays.length; j++) {
            _this.matchObj[i].PlayText += _this.matchObj[i].Plays[j].Position;
          }
        }
      });
    },
    // 點選玩法
    choosePlay: function choosePlay(play, index, btnIndexs) {
      var _this2 = this;

      this.matchObj[index].Plays.forEach(function (el) {
        if (el.isClick == 1) {
          el.isClick = 0;
          _this2.active = 0;
        }
      });
      this.active = 0;
      this.matchObjIndex = index;
      this.matchObj[this.matchObjIndex].Plays[btnIndexs].isClick = 1;
      this.active = this.matchObj[this.matchObjIndex].Plays[btnIndexs].isClick; // 把全部按鈕都放進同一個陣列

      var playArray = [];
      this.matchObj.forEach(function (el) {
        el.Plays.forEach(function (play) {
          playArray.push(play);
        });
      }); // 過濾按鈕陣列，isClick == 1 的才留下

      var filterPlayAry = playArray.filter(function (el) {
        return el.isClick > 0;
      }); // 取過濾出來的PlayId

      this.newPlayAry = filterPlayAry.map(function (el) {
        return el.PlayId;
      });
    },
    //送出
    send: function send() {
      var _this3 = this;

      var data = {
        ClubActivityId: this.clubActivityId,
        PlayIds: this.newPlayAry
      };
      apiActivityBets(data, true).then(function (res) {
        if (Math.abs(res.Code) >= 600) {
          var vm = _this3;
          var m = vm.$message({
            message: res.Message,
            duration: 3000,
            type: "error",
            center: true
          });
          setTimeout(function () {
            return m.close();
          }, 2000);
          return;
        } else {
          var _vm = _this3;

          var _m = _vm.$message({
            message: _this3.$t('BettingSuccess'),
            duration: 3000,
            type: "success",
            center: true
          });

          setTimeout(function () {
            return _m.close();
          }, 2000);
          setTimeout(function () {
            localStorage.setItem("categoryIndex", 1);

            _this3.$router.push("/ClubActivities");
          }, 3000);
        }
      });
    }
  },
  beforeDestroy: function beforeDestroy() {
    localStorage.setItem("categoryIndex", 1);
  }
};
