import { apiBuyChallengeTicket } from "@/api/match";
import back from "../../components/Botton/back.vue";
import FlowAd from "@/components/FlowAd";
import { apiGetFlowAdByType } from "@/api/FlowAd.js";
export default {
  components: {
    back: back,
    FlowAd: FlowAd
  },
  data: function data() {
    return {
      isOpenAd: false,
      adTypeId: 0,
      AdObj: {}
    };
  },
  methods: {
    buy: function buy() {
      var _this = this;

      var data = {
        matchId: this.$route.params.id
      };
      apiBuyChallengeTicket(data, true).then(function (res) {
        if (res.IsSuccess) {
          _this.$message({
            message: "购买成功",
            duration: 3000,
            type: "success",
            center: true
          });

          setTimeout(function () {
            _this.$router.push("/challengematch");
          }, 2000);
        } else if (Math.abs(res.Code) >= 600) {
          _this.$message({
            message: res.Message,
            duration: 3000,
            type: "error",
            center: true
          }); // 彈出廣告


          setTimeout(function () {
            _this.adTypeId = 2;
            var data = {
              flowAdTypeId: _this.adTypeId
            };
            apiGetFlowAdByType(data, true).then(function (res) {
              if (res.Data.Id == 0) {
                _this.isOpenAd = false;
              } else {
                _this.isOpenAd = true;
                _this.AdObj = res.Data;
              }
            });
          }, 1000);
        }
      });
    }
  }
};
