import { apiGetUserAddress, apiAddEditMemAddress } from '@/api/User.js';
var address = {
  // 库
  state: {
    addressList: [],
    addAddress: []
  },
  // 改变state内的值
  mutations: {
    // 地址列表
    changeAddressList: function changeAddressList(state, payload) {
      state.addressList = payload;
    }
  },
  //异步请求数据
  actions: {
    asyncChangeAddressList: function asyncChangeAddressList(context, payload) {
      apiGetUserAddress(payload, true).then(function (res) {
        //提交mutations里的方法
        context.commit('changeAddressList', res.Data);
      });
    }
  }
};
export default address;
