export default {
  data: function data() {
    return {
      isRed: false,
      isBlue: false,
      isBlack: false,
      isYellow: false,
      theme: ""
    };
  },
  created: function created() {
    this.loadColorTheme();
  },
  methods: {
    changeColor: function changeColor(color) {
      var token = localStorage.getItem("token");

      if (!token) {
        var m = this.$message({
          message: this.$t("Please_Login"),
          // 【 请登录 】
          duration: 1000,
          type: "error",
          center: true
        });
        setTimeout(function () {
          return m.close();
        }, 1200);
        return;
      } else {
        //初始化
        this.isRed = false;
        this.isBlue = false;
        this.isBlack = false;
        this.isYellow = false;

        switch (color) {
          case "red":
            this.isRed = true;
            break;

          case "blue":
            this.isBlue = true;
            break;

          case "black":
            this.isBlack = true;
            break;

          case "yellow":
            this.isYellow = true;
            break;
        }

        this.theme = color;
        window.document.documentElement.setAttribute("data-theme", this.theme);
        localStorage.setItem("theme", this.theme);
        this.$parent.$parent.handlePlay();
      }
    },
    // 刷新頁面維持按扭不會重置
    loadColorTheme: function loadColorTheme() {
      //如果一開始theme為空或是theme值不存在，則預設紅色
      if (localStorage.getItem("theme") == "" || localStorage.getItem("theme") == null) {
        this.isRed = true;
      }

      if (localStorage.getItem("theme") == "red") {
        this.isRed = true;
      }

      if (localStorage.getItem("theme") == "blue") {
        this.isBlue = true;
      }

      if (localStorage.getItem("theme") == "black") {
        this.isBlack = true;
      }

      if (localStorage.getItem("theme") == "yellow") {
        this.isYellow = true;
      }
    }
  }
};
