import { mapState } from "vuex";
import { bgmPlayer } from "@/utils/medias"; // import { apiGetMemClubs } from "@/api/Club.js";

export default {
  //页面加载方法
  provide: function provide() {
    return {
      reload: this.reload
    };
  },
  data: function data() {
    return {
      style: "",
      //页面加载方法
      isRouterAlive: true,
      transitionName: "slide-right",
      // 換膚用-讀取localStorage裡的換膚參數
      localStorageTheme: "",
      isOn: true,
      nowRouter: ""
    };
  },
  created: function created() {
    // 判斷裝置語系 設定語系
    if (!localStorage.getItem("lang")) {
      localStorage.setItem("lang", JSON.stringify({
        name: "简体中文",
        tag: "zh-CN"
      }));
    }

    this.getLocalStorageTheme();

    if (!localStorage.getItem("isOn")) {
      localStorage.setItem("isOn", true);
    }

    this.isOn = localStorage.getItem("isOn") == "true";

    if (isIOS) {
      localStorage.setItem("isOn", false);
      this.isOn = false;
    }

    this.handlePlay();
  },
  mounted: function mounted() {
    this.nowRouter = this.$router.currentRoute.name;

    if (window.plus) {
      this.plusReady();
    } else {
      document.addEventListener('plusready', this.plusReady, false);
    }
  },
  methods: {
    setTheme: function setTheme(name) {
      this.style = localStorage.style = name;
      var theme = {
        primary: ""
      };

      switch (name) {
        case "default":
          theme.primary = "#c12f3e";
          break;

        case "blue":
          theme.primary = "#2f7bc1";
          break;
      }

      var root = document.documentElement;
      root.style.setProperty("--primary-color", theme.primary);
    },
    //页面加载方法
    reload: function reload() {
      this.isRouterAlive = false;
      this.$nextTick(function () {
        this.isRouterAlive = true;
      });
    },
    // 讀取localStorageTheme值並換膚
    getLocalStorageTheme: function getLocalStorageTheme() {
      if (localStorage.getItem("theme") == null) {
        localStorage.setItem("theme", "blue");
      }

      this.localStorageTheme = localStorage.getItem("theme");
      window.document.documentElement.setAttribute("data-theme", this.localStorageTheme);
    },
    // 播放/靜音開關
    audioSwitch: function audioSwitch() {
      localStorage.setItem("isOn", !this.isOn);
      this.isOn = !this.isOn;
      this.handlePlay();
    },
    handlePlay: function handlePlay() {
      var theme = localStorage.getItem("theme");

      if (this.isOn) {
        bgmPlayer.stop();
        bgmPlayer.play(theme);
      } else {
        bgmPlayer.stop();
      }
    },
    plusReady: function plusReady() {
      var _this = this;

      this.webview = window.plus.webview.currentWebview();
      window.plus.key.addEventListener('backbutton', function () {
        _this.webview.canBack(function (event) {
          if (event.canBack) {
            _this.webview.back();
          } else {
            if (_this.first) {
              if (Date.now() - _this.first < 1500) {
                window.plus.runtime.quit();
              } else {
                _this.first = Date.now();

                _this.$toast('再按一次退出应用');
              }
            } else {
              _this.first = Date.now();

              _this.$toast('再按一次退出应用');
            }
          }
        });
      });
    }
  },
  watch: {
    $route: function $route(to, from) {
      // 切换动画
      var isBack = this.$router.isBack; // 监听路由变化时的状态为前进还是后退

      if (isBack) {
        this.transitionName = "slide-left";
      } else {
        this.transitionName = "slide-right";
      }

      this.$router.isBack = false;
      this.nowRouter = to.name;
    },
    style: function style(val) {
      this.setTheme(val);
    }
  }
};
