import back from "@/components/Botton/back.vue";
import { apiGetMemLogTypes, apiGetMemFundLogs } from "@/api/Club.js";
export default {
    components: {
        back
    },
    data() {
        return {
            filter:　false,
            typeId: "0",
            indexNum: 1,
            totalPages: "",
            count: 5,
            cashFlowLog: [],
            cashFlowCategory: [],
            busy: false,
            immediate: false,
            loading: false,
            noData: false,
        }
    },
    created() {
        this.getMemLogTypes();
        this.getMemFundLogs();
    },
    methods: {
        // 獲取類別列表
        getMemLogTypes() {
            let data = {
                rptId: 0
            }
            apiGetMemLogTypes(data, true).then(res => {
                if (res.Code == "600" || res.data.length == 0) {
                    this.loading = false;
                    this.noData = true;
                    return
                }
                this.cashFlowCategory = res.data;
            });
        },

        // 獲取金流列表
        getMemFundLogs() {
            this.loading = true;
            this.noData = false;
            let data = {
                typeId: this.typeId,
                index: this.indexNum,
                count: 10 
            };
            apiGetMemFundLogs(data, true).then(res => {
                if (res.Code == "600" || res.data.Data.length == 0) {
                    this.loading = false;
                    this.noData = true;
                    return
                }
                this.loading = false;
                this.noData = false;
                this.cashFlowLog = res.data.Data;
                this.totalPages = res.data.TatolPages;
            });
        },

        // 點選類別
        clickType(typeId) {
            this.cashFlowLog = [];
            this.indexNum = 1;
            this.typeId = typeId;
            this.getMemFundLogs();
            this.filter = false;
        },

        // 無限加載
        load() {
            console.log(this.totalPages)
            if(this.indexNum >= this.totalPages) {
                return
            }
            this.busy = true;
            this.indexNum++;
            let data = {
                typeId: this.typeId,
                index: this.indexNum,
                count: 10 
            };
            this.loading = true;
            apiGetMemFundLogs(data, true).then(res => {
                if (res.Code == "600" || res.data.Data.length == 0) {
                    this.loading = false;
                    this.busy = true;
                    return
                } else {
                    res.data.Data.forEach(el => {
                        this.cashFlowLog.push(el);
                        this.busy = false;
                    });
                    this.loading = false;
                    this.noData = false;
                }
            });
        }
    },
}
