import back from "@/components/Botton/back.vue";
import model from "@/components/model";
import { apiBuyClubActivityTicket, apiUpdateClubActivityStatus, apiGetClubActivityList} from "@/api/ClubActivitys.js";
import { apiGetClubGamblingList, apiUpdateClubGamblingsStatus} from "@/api/ClubGamblings.js";
export default {
  components: {
    back,
    model
  },
  data() {
    return {
      isManagement: 0,
      clubId: undefined,
      isOpen: false,

      activeCategoryIndex: 0,
      tabIndex: 0,
      betTabIndex: 0,
      latestActivitiesObj: [],
      historyActivitiesObj: [],
      isTicketTipOpen: false,
      ticketTipText:
        "活动规则：<br />1、胜负的判断：<br />①根据最终比赛结果来判定对应的竞猜胜负<br />②根据本次活动竞猜正确的胜率来判断第一名<br />2、参与规则<br />①参与需缴纳一定金额的门票<br />②购买门票的用户，视作愿意参与活动，门票不予退还<br />3、奖励规则<br />①胜利最高的为第一名，胜率想同的用户并列第一<br />②第一名的用户获得本次活动门票收益,如有多个用户并列第一，则均分本次活动的奖励<br />③奖励的发放在活动结束后的2个工作日内，由系统自动发放奖励<br />活动最终解释权归本APP所有",
      avtiveId: "",
      activeNodata: false,
      historyNoData: false,
      matchName: "",
      // 投注活動
      betMyList: [],
      betOtherList: [],
      betMyListNoData: false,
      betOtherListNoData: false,

      wipeLoading: false,
      callBackMsg: "",

      // v-infinite-scroll 套件
      busy: false,
      immediate: false,
      lastestIndexNum: 1,
      historyIndexNum: 1,
      lastestTotalPages: "",
      historyTotalPages:"",

      // 彈窗
      isTicketTipType: "",
      deleteData:""
    };
  },
  created() {
    // 讀取是否為管理階
    this.isManagement = localStorage.getItem("isManagement");
    // 取得clubId
    this.clubId = localStorage.getItem("ClubId");


    if (localStorage.getItem("activeCategoryIndex") == null) {
      localStorage.setItem("activeCategoryIndex", 0);
    }
    if (localStorage.getItem("tabIndex") == null) {
      localStorage.setItem("tabIndex", 0);
    }
    if (localStorage.getItem("betTabIndex") == null) {
      localStorage.setItem("betTabIndex", 0);
    }

    if (localStorage.getItem("activeCategoryIndex") == "0") {
      this.getLastestActivies();
      this.activeCategoryIndex = 0;
      this.tabIndex = localStorage.getItem("tabIndex");
      if (this.tabIndex == 1) {
        this.getHistoryActivies();
      }
    } else if (localStorage.getItem("activeCategoryIndex") == "1") {
      this.activeCategoryIndex = 1;
      this.getActiveBetList();
      this.betTabIndex = localStorage.getItem("betTabIndex");
    }
  },
  methods: {
    // 獲取最新活動
    getLastestActivies() {
      let data = {
        ClubId: this.clubId,
        Type: 1,
        index: this.lastestIndexNum,
        count: 10
      }
      apiGetClubActivityList(data, true).then(res => {
        if (res.Code == "600") {
          this.isOpen = true;
          this.callBackMsg = res.Message;
          this.busy = true;
          return;
        }
        // 俱樂部活動頁的資料
        this.latestActivitiesObj = res.Data.Data;
        this.lastestTotalPages = res.Data.TotalPages;
      })
      // this.busy = false;
      this.tabIndex = 0;
      localStorage.setItem("activeCategoryIndex", this.activeCategoryIndex);
    },

    // 無限加載最新活動
    loadLastest() {
      if (this.lastestIndexNum >= this.lastestTotalPages ) {
        return
      }
      this.busy = true;
      this.lastestIndexNum++;
      let data = {
        ClubId: this.clubId,
        Type: 1,
        index: this.lastestIndexNum,
        count: 10
      }
      apiGetClubActivityList(data, true).then(res => {
        if (res.Code == "600") {
          this.isOpen = true;
          this.callBackMsg = res.Message;
          this.busy = true;
          return;
        }
        // 俱樂部活動頁的資料
        if (res.Data.Data.length == 0) {
          this.busy = true;
        } else if(res.Data.Data.length > 0){
          res.Data.Data.forEach(el => {
            this.latestActivitiesObj.push(el);
            this.busy = false;
          })
        }
      })
    },


    // 獲取歷史活動
    getHistoryActivies() {
      let data = {
        ClubId: this.clubId,
        Type: 2,
        index: this.historyIndexNum,
        count: 10
      }
      apiGetClubActivityList(data, true).then(res => {
        if (res.Code == "600") {
          this.isOpen = true;
          this.callBackMsg = res.Message;
          return;
        }
        // 俱樂部活動頁的資料
        this.historyActivitiesObj = res.Data.Data;
        this.historyTotalPages = res.Data.TotalPages;
      })
      this.tabIndex = 1;
      localStorage.setItem("activeCategoryIndex", this.activeCategoryIndex);
    },

    // 無限加載歷史活動
    loadHistory() {
      if (this.historyIndexNum >= this.historyTotalPages ) {
        return
      }
      this.busy = true;
      this.historyIndexNum++;
      let data = {
        ClubId: this.clubId,
        Type: 2,
        index: this.historyIndexNum,
        count: 10
      }
      apiGetClubActivityList(data, true).then(res => {
        if (res.Code == "600") {
          this.isOpen = true;
          this.callBackMsg = res.Message;
          this.busy = true;
          return;
        }
        // 俱樂部活動頁的資料
        if (res.Data.Data.length == 0) {
          this.busy = true;
        } else if(res.Data.Data.length > 0){
          res.Data.Data.forEach(el => {
            this.historyActivitiesObj.push(el);
            this.busy = false;
          })
        }
      })
    },

    // 取得俱樂部投注列表
    getActiveBetList() {
      let data = {
        ClubId: this.clubId
      };
      apiGetClubGamblingList(data, true).then(res => {
        this.betMyList = res.Data.MyGamblings;
        this.betOtherList = res.Data.OtherGamblings;
        if (this.betMyList == null || this.betMyList.length == 0) {
          this.betMyListNoData = true;
        }
        if (this.betOtherList == null || this.betOtherList.length == 0) {
          this.betOtherListNoData = true;
        }
      });
       this.betTabIndex = 0;
      localStorage.setItem("activeCategoryIndex", this.activeCategoryIndex);
    },

    // 切換俱樂部挑戰分頁
    changeTabIndex(index) {
      this.tabIndex = index;
      localStorage.setItem("tabIndex", index);
      // 最新
      if (index == 0) {
        this.latestActivitiesObj = [];
        this.lastestIndexNum = 1;
        this.busy = false;
        this.getLastestActivies();
      }
      // 歷史
      if (index == 1) {
        this.historyActivitiesObj = [];
        this.historyIndexNum = 1;
        this.busy = false;
        this.getHistoryActivies();
      }
    },
    // 切換俱樂部投注分頁
    changeBetTabIndex(index) {
      this.betTabIndex = index;
      localStorage.setItem("betTabIndex", index);
    },

    // 活動開啟發佈頁面
    activesRelease() {
      this.$router.push('/ClubActivitiesRelease');
    },

    // 購買活動門票
    getTicket() {
      let data = {
        ClubId: this.clubId,
        ClubActivityId: this.avtiveId
      };
      apiBuyClubActivityTicket(data, true).then(res => {
        if (res.Code == 600) {
          let vm = this;
          const m = vm.$message({
            message: res.Message,
            duration: 3000,
            type: "error",
            center: true
          });
          setTimeout(() => m.close(), 2000);
          return;
        }
        localStorage.setItem("AcvtiveId", this.avtiveId);
        let vm = this;
        const m = vm.$message({
          message: this.$t('SuccessfulPurchase'),
          duration: 3000,
          type: "success",
          center: true
        });
        setTimeout(() => m.close(), 2000);
        setTimeout(() => {
          this.isTicketTipOpen = false;
          this.$router.push("/ClubActivityMatch");
        }, 3000);
      });
    },

    // 繼續下注
    continueBet(avtiveId) {
      localStorage.setItem("AcvtiveId", avtiveId);
      this.$router.push("/ClubActivityMatch");
    },

    // 參加成功
    goResultDetail(avtiveId){
      localStorage.setItem("AcvtiveId", avtiveId);
      this.$router.push("/ActivityMatchResultDetail");
    },

    // 刪除最新活動
    deleteActive(){
      let data = {
          Id: this.deleteData.Id,
          ClubId: this.deleteData.ClubId,
          SelectLeague: this.deleteData.SelectLeague,
          MatchIds: this.deleteData.MatchIds,
          PlayIds: this.deleteData.PlayIds,
          ActivityContent: this.deleteData.ActivityContent,
          NumGames: this.deleteData.NumGames,
          ActivityImg: this.deleteData.ActivityImg,
          SumBonus: this.deleteData.SumBonus,
          Status: -1,
          Stime: this.deleteData.Stime,
          Etime: this.deleteData.Etime,
          CreateTime: this.deleteData.CreateTime,
          Creator: this.deleteData.Creator,
      };
      apiUpdateClubActivityStatus(data, true).then(res => {
        if (res.Success) {
          let vm = this;
          const m = vm.$message({
            message: this.$t('successfullyDeleted'),
            duration: 3000,
            type: "success",
            center: true
          });
          setTimeout(() => m.close(), 2000);
          this.getLastestActivies();
        } else {
          let vm = this;
          const x = vm.$message({
            message: res.Message,
            duration: 3000,
            type: "warning",
            center: true
          });
          setTimeout(() => x.close(), 2000);
        }
        this.isTicketTipOpen = false;
      });
    },

    // 前往俱樂部投注
    goBet(id) {
      localStorage.setItem("betId", id);
      setTimeout(() => {
        this.$router.push("/clubActiveBet");
      }, 250);
    },

    // 查看單一賽果 type:  1 = 發起人賽果;  2 = 其他人賽果
    goBetResult(type, id) {
      localStorage.setItem("betId", id);
      localStorage.setItem("singleResult", true)
      if (type == 1) {
        setTimeout(() => {
          this.$router.push("/InitiatorBetResult");
        }, 250);
      } else {
        setTimeout(() => {
          this.$router.push("/BetResult");
        }, 250);
      }
    },

    // 俱樂部投注刪除
    betDelete() {
      let data = {
        Id: this.deleteData.Id,
        ClubId: this.deleteData.ClubId,
        MatchId: this.deleteData.MatchId,
        MatchItemId: this.deleteData.MatchItemId,
        PlayId: this.deleteData.PlayId,
        BetPosition: this.deleteData.BetPosition,
        OddsValue: this.deleteData.OddsValue,
        GamblingTitle: this.deleteData.GamblingTitle,
        GamblingContent: this.deleteData.GamblingContent,
        GamblingImg: this.deleteData.GamblingImg,
        Deposit: this.deleteData.Deposit,
        OpenBetAmount: this.deleteData.OpenBetAmount,
        BettedAmount: this.deleteData.BettedAmount,
        HandlingCharge: this.deleteData.HandlingCharge,
        Status: -1,
        Stime: this.deleteData.Stime,
        Etime: this.deleteData.Etime,
        CreateTime: this.deleteData.CreateTime,
        Creator: this.deleteData.Creator
      }
      apiUpdateClubGamblingsStatus(data, true).then(res => {
        if(res.Data.Item1) {
          let vm = this;
          const m = vm.$message({
            message: this.$t('successfullyDeleted'),
            duration: 3000,
            type: "success",
            center: true
          });
          setTimeout(() => m.close(), 2000);
          // 再獲取一次列表
          this.getActiveBetList();
          this.isTicketTipOpen = false;
          return;
        } 
        if (res.Code == 600) {
          let vm = this;
          const m = vm.$message({
            message: res.Message,
            duration: 3000,
            type: "warning",
            center: true
          });
          setTimeout(() => m.close(), 2000);
          this.isTicketTipOpen = false;
          return;
        }
        if (res.Data.Item1 == false) {
          let vm = this;
          const m = vm.$message({
            message: this.$t("DataError"),
            duration: 3000,
            type: "warning",
            center: true
          });
          setTimeout(() => m.close(), 2000);
          return;
        }
        this.isTicketTipOpen = false;
      });
    },
    //刪除活動function選擇
    deleteFunction () {
      this.isTicketTipType == 'delete' ? this.deleteActive() : this.betDelete()
    },

    //彈窗顯示
    isTicketTipShow (e,delData) {
      this.isTicketTipType = e.toElement.className;
      this.deleteData = delData;
      this.isTicketTipOpen = !this.isTicketTipOpen;

    },
    // 彈窗確認按鈕事件
    confirmBtnEvent() {
      this.$router.push("/CreateClub");
    },
  }
};
