import NavBottom from "@/components/Main/NavBottom.vue";

import {
    apiGetMatchs,
    apiGetMyMatchList,
    apiGetRankMatchList
} from "../api/match.js";

export default {
    created() {
        // this.showmatchlist2();
        this.lang = JSON.parse(localStorage.getItem("lang")).tag;
        this.getrankmatch();
        this.getMyrank();
    },
    data() {
        return {
            ShowExplain: false,
            test11: true,
            test22: false,
            bgc1: false,
            matchId: {
                matchTypeId: "2",
                index: 1
            },
            matchlist: [],
            testlist: [1],
            myranklist: [],
            lang: "",
            wipeLoading: true,
            lefttabNomatchs: false,
            righttabNomatchs: false
        };
    },
    components: {
        NavBottom
    },
    methods: {
        //跳转排位赛事
        getrankmatch() {
            let data = {
                index: 1,
                count: 100,
                isAll: true
            };
            apiGetRankMatchList(data, true).then(res => {
              if(res.Data){
                this.wipeLoading = false;
                this.matchlist = res.Data;
                res.Data.length > 0 ? this.righttabNomatchs = false : this.righttabNomatchs = true;
              }
            });
        },
        gorankmatch(id, name) {
            this.$refs.son.show = false;
            this.$store.commit("upMatchid", {
                MatchId: id,
                MatchName: name
            });
            this.$router.push("/rankmatch");
        },
        //排位赛选择
        goChoose() {
            this.$router.push("/rankchoose");
        },
        //获得个人排位赛信息
        getUserRankInfo() {
            this.$refs.son.show = false;
            this.$store.dispatch("asyncGetUserRankInfo");
        },
        showmatchlist2() {
            let data = {
                matchTypeId: "2",
                index: 1,
                count: 100
            };
            this.$store.dispatch("asyncgetMatchList", data);
        },
        //我的排位
        getMyrank() {
            let data = {
                index: 1,
                count: 100
            };
            apiGetMyMatchList(data, true).then(res => {
              if(res.Data){
                this.wipeLoading = false;
                this.myranklist = res.Data;
                res.Data.length > 0 ? this.lefttabNomatchs = false : this.lefttabNomatchs = true;
              }
            });
        },
        //跳转
        goJump(id, name) {
            this.$store.commit("upMatchid", {
                MatchId: id,
                MatchName: name
            });
            this.$router.push("/ranklistdetails");
        },
        //列表
        getmatchlist() {
            this.$refs.son.show = false;
            this.$store.dispatch("asyncGetHistoryMatchList");
        },
        //  tab栏
        //左
        test1() {
          this.matchlist = [];
          this.myranklist = [];
          this.wipeLoading = true;
          this.righttabNomatchs = false;
          this.lefttabNomatchs = false;
          this.getMyrank();
          this.$refs.son.show = false;
          this.test11 = true;
          this.test22 = false;
          this.bgc1 = false;
          let data = {
              test11: true,
              test22: false,
              bgc1: false
          };
          this.$store.commit("upBoolen", data);
          // alert(1);
        },
        // tab栏
        test2() {
          this.matchlist = [];
          this.myranklist = [];
          this.wipeLoading = true;
          this.righttabNomatchs = false;
          this.lefttabNomatchs = false;
          this.getrankmatch();
          this.$refs.son.show = false;
          this.test11 = false;
          this.test22 = true;
          this.bgc1 = true;
          let data = {
              test11: false,
              test22: true,
              bgc1: true
          };
          this.$store.commit("upBoolen", data);
          // alert(2);
        },
        showPopup() {
            this.$refs.son.show = false;
            this.ShowExplain = true;
        },
        hideExplain() {
            this.$refs.son.show = false;
            this.ShowExplain = false;
        }
    }
};
