    this.uname = localStorage.getItem('uname');

    // 獲取俱樂部列表
    this.getClubList();
  },

  methods: {
    toCreatRule() {
      this.$router.push("/searchClubRule");
    },

    // 獲取俱樂部列表
    getClubList() {
      let data = {
        ClubId: 0
      };
      apiGetClubs(data, true).then(res => {
        this.GeneralList = res.Data.RecommendList;
      });
    },

    // 搜尋
    searchClub(val) {
      if (val == "") {
        this.getClubList();
        return;
      }
      this.getClubList();
      this.searchList = this.GeneralList.filter(clubName => {
        const regex = new RegExp(val, "gi");
        return clubName.ClubName.match(regex);
      });
      this.GeneralList = [];
      this.GeneralList = this.searchList;
    },

    // 申請加入俱樂部
    joinClub() {
      let data = {
        ClubId: this.clickClubId,
        UName: this.uname,
        Type: 0    // 0 申请进入  1 退出  2 被踢
    }
