import { apiBuyChallengeTicket } from "@/api/match";
import back from "../../components/Botton/back.vue";
import FlowAd from "@/components/FlowAd";
import { apiGetFlowAdByType} from "@/api/FlowAd.js";
export default {
  components: {
    back,
    FlowAd
  },
  data() {
    return {
      isOpenAd: false,
      adTypeId: 0,
      AdObj: {},
    }
  },
  methods: {
    buy() {
      let data = {
        matchId: this.$route.params.id
      };

      apiBuyChallengeTicket(data, true).then(res => {
        if (res.IsSuccess) {
          this.$message({
            message: "购买成功",
            duration: 3000,
            type: "success",
            center: true
          });
          setTimeout(() => {
            this.$router.push("/challengematch");
          }, 2000);
        } else if (Math.abs(res.Code) >= 600) {
          this.$message({
            message: res.Message,
            duration: 3000,
            type: "error",
            center: true
          });
          // 彈出廣告
          setTimeout(() => {
            this.adTypeId = 2;
            let data = {
              flowAdTypeId: this.adTypeId
            };
            apiGetFlowAdByType(data, true).then(res => {
              if(res.Data.Id == 0) {
                this.isOpenAd = false;
                } else {
                this.isOpenAd = true;
                this.AdObj = res.Data;
              }
            });
          }, 1000);
        }
      });
    }
  }
};
