import back from "@/components/Botton/back.vue";
import model from "@/components/model";
import { apiGetClubBonusRpt, apiReceiveBonus } from "@/api/Club";
export default {
    components: {
        back,
        model
    },
    data () {
        return {
            clubBonusRptList: [],
            isLoading: true,
            noMission: false,
            busy: false,
            IndexNum: 1,
            isDividendBoxOpen: false,
            itemId: "",
            tatolPages: 1
        }
    },
    methods:{
        load() {
        this.busy = true;
        let data = {
            index: this.IndexNum,
            count: 10
        };
        this.getClubBonusRpt(data);
        },
        getClubBonusRpt(Data) {
            apiGetClubBonusRpt(Data ,true).then(res =>{
                if(res.data.Data.length == 0 || Math.abs(res.Code) >= 600){
                    this.isLoading = false;
                    this.busy = true;
                    if (this.clubBonusRptList.length == 0) {
                        
                        this.noMission = true;
                    }
                }else {
                    this.tatolPages = res.data.TatolPages;
                    this.busy = false;
                    if (this.IndexNum == res.data.TatolPages) {
                        this.busy = true;
                    }
                    this.isLoading = false;
                    for (let i = 0; i < res.data.Data.length; i++) {
                        this.clubBonusRptList.push(res.data.Data[i]);
                    }
                    this.IndexNum++;
                    
                }
            })
        },
        dividend(Id) {
            let dividendData = {
                rptId: Id
            }
            apiReceiveBonus(dividendData, true).then(res => {
                if(Math.abs(res.Code) >= 600) {
                    let vm = this;
                    const m = vm.$message({
                        message: res.data.message,
                        duration: 3000,
                        type: "error",
                        center: true
                    });
                    setTimeout(() => {m.close()}, 2000);
                    this.isDividendBoxOpen = false;
                    return
                }else {
                    let vm = this;
                    const m = vm.$message({
                        message: res.data.message,
                        duration: 3000,
                        type: "success",
                        center: true
                    });
                    setTimeout(() => {m.close()}, 2000);
                    this.isLoading = true;
                    this.clubBonusRptList= [];
                    this.IndexNum = 1;
                    let data = {
                        index: this.IndexNum,
                        count: 10
                    };
                    this.getClubBonusRpt(data);
                    this.isDividendBoxOpen = false;
                }
            })
        }
    }
}
