export default {
  props: {
    placeholder: String,
    value: String,
    clear: Boolean,
    password: Boolean,
    account: Boolean,
    phone: Boolean,
    captcha: Boolean
  },
  data() {
    return {
      isActive: true,
      togglePassword: false,
      val: ""
    };
  },
  methods: {
    clearData() {
      this.val = "";
      this.$emit("update:value", "");
    },
    handleInput(e) {
      this.$emit("update:value", e.target.value);
    }
  },
  computed: {
    ifPassword() {
      return this.togglePassword ? "password" : "text";
    }
  }
};
