import back from "@/components/Botton/back.vue";
import { apiGetClubActivityMatchResult } from "@/api/ClubActivitys.js";
export default {
  components: {
    back
  },
  data() {
    return {
      clubId: "",
      matchResultObj: [],
      noData: false,
      isLoading: false,

      // v-infinite-scroll 套件
      busy: false,
      immediate: false,
      indexNum: 1,
      totalPages: undefined,
    };
  },
  created() {
    localStorage.setItem("categoryIndex", 1);
    this.clubId = localStorage.getItem("ClubId");
    this.getMatchResult();
  },
  methods: {
    getMatchResult() {
      let data = {
        ClubId: this.clubId,
        index: this.indexNum,
        count: 10,
      };
      this.isLoading = true;
      this.noData = false;
      apiGetClubActivityMatchResult(data, true).then(res => {
        if (res.Data.Data.length == 0 || res.Data.Data == false){
          this.noData = true;
        } else {
          this.matchResultObj = res.Data.Data;
          this.totalPages = res.Data.TotalPages;
          this.isLoading = false;
          this.noData = false;
        } 
      });
    },
    goResultDetail(item) {
      localStorage.setItem("AcvtiveId", item.Id);
      this.$router.push("/ActivityMatchResultDetail");
    },

    // 無限加載
    load() {
      if (this.indexNum >= this.totalPages) {

        this.busy = true;
        return;
      }
      this.indexNum++;
      let data = {
        ClubId: this.clubId,
        index: this.indexNum,
        count: 10,
      }
      apiGetClubActivityMatchResult(data, true).then(res => {
        res.Data.Data.forEach(el => {
          this.matchResultObj.push(el);
        });
      });
    }
  },
  beforeDestroy() {
    localStorage.setItem("categoryIndex", 1);
  }
};
