import DialogComp from "./index.vue";
import { apiSign } from "@/api/User.js";
import Vue from "vue";
import { Toast } from "vant";
import FlowAd from "../FlowAd.vue";
import { apiGetFlowAdByType} from "@/api/FlowAd.js";
Vue.use(Toast);
export default {
  name: "SignIn",
  props: {
    showModal: {
      type: Boolean,
      default: false
    }
  },
  components: {
    DialogComp,
    FlowAd
  },
  filters: {
    numDay(num) {
      if ((num = null)) {
        return 0;
      } else {
        return num;
      }
    }
  },
  data() {
    return {
      srcImg1: require("@/assets/iconI_06.png"),
      srcImg2: require("@/assets/iconI_06.png"),
      srcImg3: require("@/assets/ii_03.png"),
      srcImg4: require("@/assets/iconI_06.png"),
      srcImg5: require("@/assets/iconI_06.png"),
      srcImg6: require("@/assets/iconI_06.png"),
      srcImg7: require("@/assets/ii_03.png"),
      days: Array(7),
      money: 2,
      singSuccess: false,
      day: 0,
      list: [],
      listMsg: {
        ContinuousSignDays: 0
      },
      bgColor1: "#e4e4e4",
      bgColor2: "#e4e4e4",
      bgColor3: "#e4e4e4",
      bgColor4: "#e4e4e4",
      bgColor5: "#e4e4e4",
      bgColor6: "#e4e4e4",
      bgColor7: "#e4e4e4",
      num: 0,
      feng: this.showModal,
      isOpenAd: false,
      adTypeId: 0,
      AdObj: {},
    };
  },
  methods: {
    hide() {
      // 彈出廣告
      this.adTypeId = 3;
      let data = {
        flowAdTypeId: this.adTypeId
      };
      apiGetFlowAdByType(data, true).then(res => {
        if(res.Data.Id == 0) {
          this.isOpenAd = false;
          } else {
          this.isOpenAd = true;
          this.AdObj = res.Data;
        }
      });
      let self = this;
      this.$emit("update:showModal", false);
      this.feng = false;
    },
    getDayDate() {
      let data = {};
      let self = this;
      apiSign(data, true).then(res => {
        if (res.code == 200) {
          self.feng = true;
          this.$emit("update:showModal", true);
          Toast({ message: res.message, duration: 1000 });
          this.listMsg.ContinuousSignDays = res.data.ContinuousSignDays;
          if (this.listMsg.ContinuousSignDays == 1) {
            this.getNum1();
          }
          if (this.listMsg.ContinuousSignDays == 2) {
            this.getNum1();
            this.getNum2();
          }
          if (this.listMsg.ContinuousSignDays == 3) {
            this.getNum1();
            this.getNum2();
            this.getNum3();
          }
          if (this.listMsg.ContinuousSignDays == 4) {
            this.getNum1();
            this.getNum2();
            this.getNum3();
            this.getNum4();
          }
          if (this.listMsg.ContinuousSignDays == 5) {
            this.getNum1();
            this.getNum2();
            this.getNum3();
            this.getNum4();
            this.getNum5();
          }
          if (this.listMsg.ContinuousSignDays == 6) {
            this.getNum1();
            this.getNum2();
            this.getNum3();
            this.getNum4();
            this.getNum5();
            this.getNum6();
          }
          if (this.listMsg.ContinuousSignDays == 7) {
            this.getNum1();
            this.getNum2();
            this.getNum3();
            this.getNum4();
            this.getNum5();
            this.getNum6();
            this.getNum7();
          }
        } else {
          this.listMsg.ContinuousSignDays = res.data.ContinuousSignDays;
          if (this.listMsg.ContinuousSignDays == 1) {
            this.getNum1();
          }
          if (this.listMsg.ContinuousSignDays == 2) {
            this.getNum1();
            this.getNum2();
          }
          if (this.listMsg.ContinuousSignDays == 3) {
            this.getNum1();
            this.getNum2();
            this.getNum3();
          }
          if (this.listMsg.ContinuousSignDays == 4) {
            this.getNum1();
            this.getNum2();
            this.getNum3();
            this.getNum4();
          }
          if (this.listMsg.ContinuousSignDays == 5) {
            this.getNum1();
            this.getNum2();
            this.getNum3();
            this.getNum4();
            this.getNum5();
          }
          if (this.listMsg.ContinuousSignDays == 6) {
            this.getNum1();
            this.getNum2();
            this.getNum3();
            this.getNum4();
            this.getNum5();
            this.getNum6();
          }
          if (this.listMsg.ContinuousSignDays == 7) {
            this.getNum1();
            this.getNum2();
            this.getNum3();
            this.getNum4();
            this.getNum5();
            this.getNum6();
            this.getNum7();
          }
        }
      });
    },
    getNum1() {
      this.num = 1;
      this.bgColor1 = "#feb700";
      this.srcImg1 = require("@/assets/succ.png");
    },
    getNum2() {
      this.num = 2;
      this.bgColor2 = "#feb700";
      this.srcImg2 = require("@/assets/succ.png");
    },
    getNum3() {
      this.num = 3;
      this.bgColor3 = "#feb700";
      this.srcImg3 = require("@/assets/li.png");
    },
    getNum4() {
      this.num = 4;
      this.bgColor4 = "#feb700";
      this.srcImg4 = require("@/assets/succ.png");
    },
    getNum5() {
      this.num = 5;
      this.bgColor5 = "#feb700";
      this.srcImg5 = require("@/assets/succ.png");
    },
    getNum6() {
      this.num = 6;
      this.bgColor6 = "#feb700";
      this.srcImg6 = require("@/assets/succ.png");
    },
    getNum7() {
      this.num = 7;
      this.bgColor7 = "#feb700";
      this.srcImg7 = require("@/assets/li.png");
    },
    question() {}
  },
  created() {
    let token = localStorage.getItem("token");
    if (token) {
      this.getDayDate();
    }
  }
};
