export default {
  name: "VAudio",
  props: {
    src: {
      type: String,
      default: undefined
    },
    play: {
      type: Boolean,
      default: false
    }
  },
  data() {
    return {};
  },
  watch: {
    play() {
      this.handelPlayStatus();
    }
  },
  methods: {
    handelPlayStatus() {
      if (!this.id) {
        console.error("the id of vaudio is undefined !");
        return;
      }
      var audio = document.querySelector("#bgm");
      var forcePlay = function() {
        audio.play();
      };
      if (this.play) {
        audio.play();
      } else {
        audio.pause();
        return;
      }
      document.addEventListener("touchstart", forcePlay, {once:true});
    }
  },
  mounted() {}
};
