.vue-tinder {
  position: absolute;
  z-index: 1;
  left: 0;
  right: 0;
  top: 10px;
  margin: auto;
  border-radius: 10px;
  height: calc(100% - 0.4rem);
  min-width: 300px;
  max-width: 355px;
}

.nope-pointer,
.like-pointer {
  position: absolute;
  z-index: 1;
  top: 20px;
  width: 64px;
  height: 64px;
}

.nope-pointer {
  right: 10px;
}

.like-pointer {
  left: 10px;
}

.super-pointer {
  position: absolute;
  z-index: 1;
  bottom: 80px;
  left: 0;
  right: 0;
  margin: auto;
  width: 112px;
  height: 78px;
}

.fade-enter,
.fade-leave-to {
  opacity: 0;
}

.fade-leave,
.fade-enter-to {
  opacity: 1;
}

.fade-enter-active,
.fade-leave-active {
  transition: all 1s;
}

@keyframes gif {
  0% {
    -webkit-transform: rotateY(0deg);
    transform: rotateY(0deg);
  }
  100% {
    -webkit-transform: rotateY(360deg);
    transform: rotateY(360deg);
  }
}

.go {
  -webkit-animation: gif 2s infinite linear;
  animation: gif 2s infinite linear;
}

.no-mode-translate-fade-enter-active,
.no-mode-translate-fade-leave-active {
  transition: all 0.5s;
}

.no-mode-translate-fade-enter,
.no-mode-translate-fade-leave-active {
  opacity: 0;
}

.no-mode-translate-fade-enter {
  transform: translateX(400px);
}

.no-mode-translate-fade-leave-active {
  transform: translateX(-400px);
}

.bigImgimg {
  position: fixed;
  bottom: 2rem;
  right: 0;
  z-index: 5;
}

.fade-enter-active,
.fade-leave-active {
  transition: opacity 0.5s;
}

.fade-enter,
.fade-leave-to {
  opacity: 0;
}

.fade-leave,
.fade-enter-to {
  opacity: 1;
}

.boxB {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  margin: auto;
  width: 5.5rem;
  height: 3.4rem;
  /* transform: translate(-50%,-50%); */
  animation: show 1s;
  z-index: 66;
}

.boxB .top {
  width: 100%;
  height: 0.8rem;
  background: #c12f3e;
  border-top-left-radius: 0.2rem;
  border-top-right-radius: 0.2rem;
  position: relative;
}

.boxB .top img {
  position: absolute;
  width: 4.36rem;
  height: 0.92rem;
  left: 0;
  top: -0.1rem;
  right: 0;
  bottom: 0;
  margin: auto;
}

.boxB .bottom {
  width: 100%;
  border-bottom-left-radius: 0.2rem;
  border-bottom-right-radius: 0.2rem;
  background: #f3f3f3;
  font-size: 0.3rem;
  text-align: center;
  color: #4d4d4d;
  padding-top: 0.1rem;
}

.di {
  width: 100%;
  border-top: 1px solid #d8d8d8;
  padding: 0.37rem 0 0.34rem 0;
}

.di span {
  display: inline-block;
  width: 2.1rem;
  height: 0.6rem;
  border-radius: 0.4rem;
  text-align: center;
  line-height: 0.6rem;
  font-size: 0.24rem;
  color: #fff;
  box-shadow: 1px 3px 6px #d8d8d8;
}
