import back from "@/components/Botton/back.vue";
import { apiGetAllClubs, apiJoinClub } from "@/api/Club.js";
import model from "@/components/model";
import { apiGetMatchs } from "@/api/match";
export default {
  components: {
    back,
    model
  },
  data() {
    return {
      category: [],
      categoryType: 0,
      isOpen: false,
      isMember: 0,

      uname: "",
      clickClubId: undefined,
      isError: false,
      errorMsg: "",
      clubList: [],
      searchList: [],
      searchVal: "",
      matchId: "",

      IndexNum: 1,
      // 防止连点
      isDisable: false,

      noMission: false,

      // v-infinite-scroll 套件
      busy: false,
      immediate: false,
      vIndex: 1,
    };
  },

  created() {
    // 判斷是否有會員 1 = 有  2 = 無
    this.isMember = localStorage.getItem("isMember");

    // 獲取使用者名稱
    this.uname = localStorage.getItem("uname");

    // 獲取联赛列表
    this.matchType();
  },
  mounted() {
    window.addEventListener("scroll", this.handleScroll, true);
  },
  methods: {

    // 右移無限加載
    handleScroll() {
      let category = document.getElementById("category");
      let categoryWrap = document.getElementById("categoryWrap");
      let categoryWidth = category.getBoundingClientRect().width;
      let categoryWrapWidth = categoryWrap.getBoundingClientRect().width;
      let scrollMove = category.scrollLeft;
      let moveOffset = categoryWrapWidth - categoryWidth;

      if (scrollMove == moveOffset) {
        this.IndexNum++;
        this.matchType();
      }
    },
    toCreatRule() {
      this.$router.push("/CreateClubRule");
    },

    // 獲取導覽賽事種類
    matchType() {
      let data1 = {
        matchTypeId: 2,
        index: this.IndexNum,
        count: 10,
        havaPlay: true,
        matchItemTypeId: -1
      };
      apiGetMatchs(data1, true).then(res => {
        res.Data.forEach(el => {
          this.category.push(el)
        });
      }).then(() => {
        this.getCategoryClubs(this.category[0].MatchId)
      });
    },
    // 初始化
    init() {
      this.matchId = 0;
      this.vIndex = 1;
      this.clubList= [];
    },
    // 獲取賽事種類的俱樂部清單
    getCategoryClubs(matchId, index) {
      this.init();
      if (index !== 0) {
        this.busy = true;
      }
      this.matchId = matchId;
      let data = {
        MatchId: this.matchId,
        ClubName: "",
        index: 1,
        count: 10,
      };
      apiGetAllClubs(data, true).then(res => {
        this.clubList = res.Data.Data;
        this.busy = false;
      });
    },

    // 搜尋
    searchClub() {
      let data = {
        MatchId: this.matchId,
        ClubName: this.searchVal,
        index: 1,
        count: 10,
      };
      apiGetAllClubs(data, true).then(res => {
        this.clubList = res.Data.Data;
      });
    },

    // 申請加入俱樂部
    joinClub() {
      let data = {
        ClubId: this.clickClubId,
        UName: this.uname,
        Type: 0 // 0 申请进入  1 退出  2 被踢
      };
      apiJoinClub(data, true).then(res => {
        this.isError = true;
        this.errorMsg = res.Message;
        this.isOpen = false;
      });
    },

    //
    load() {
      this.busy = true;
      this.vIndex++;
      let data = {
        MatchId: this.matchId,
        ClubName: "",
        index: this.vIndex,
        count: 10,
      };
      apiGetAllClubs(data, true).then(res => {
        if (res.Data.Data.length == 0) {
          this.busy = true;
        } else if(res.Data.Data.length > 0){
          res.Data.Data.forEach(el => {
            this.clubList.push(el);
            this.busy = false;
          })
        }
      });
    }
  }
};
