import back from "@/components/Botton/back.vue";
import {
    apiGetUserAddress,
    apiAddEditMemAddress,
    apiDelMemAddress
} from '@/api/User.js'
import Vue from 'vue';
import {
    Area,
    Toast
} from 'vant';

Vue.use(Area).use(Toast);
export default {
    components: {
        back
    },
    filters: {
        openMo(val) {
            if (val == 1) {
                return true
            } else {
                return false
            }
        }
    },
    data() {
        return {
            openList: [],
            value2: '',
            tan: false,
        }
    },
    methods: {
        quxiao() {
            this.tan = false
        },
        goBack() {
            this.$router.go(-1)
        },
        que() {
            let data = {
                AID: this.openList[0]
            }
            apiDelMemAddress(data, true).then(res => {
                if (Math.abs(res.Code) >= 600) {
                    this.$message({
                    message: res.Message,
                    type: "error",
                    center: true,
                    duration: 3000
                    });
                } else {
                    const m = this.$message({
                        message: this.$t("successfullyDeleted"),
                        type: "success",
                        duration: 1000
                    });
                    setTimeout(() => m.close(), 1200)
                    this.$router.replace('/address')
                    this.$router.go(-1)
                }
            })
        },
        changes() {

        },
        deltel() {
            this.tan = true
        },
        commit() {
            let isMo = 0
            if (this.value2) {
                isMo = 1
            } else {
                isMo = 0
            }
            let data = {
                AId: this.openList[0],
                UId: '',
                UName: this.openList[1],
                UPhoneNumber: this.openList[2],
                URedeemAddress: this.openList[4],
                // IdentityCard: this.detailAdd,
                CreateTime: this.openList[5],
                IsDefault: isMo,
            }
            apiAddEditMemAddress(data, true).then(res => {
                if (Math.abs(res.Code) >= 600) {
                    this.$message({
                    message: res.Message,
                    type: "warning",
                    center: true,
                    duration: 3000
                    });
                } else {
                    const m = this.$message({
                        message: this.$t("SavedSuccessfully"),
                        type: "success",
                        duration: 1000
                    });
                    setTimeout(() => m.close(), 1200)
                    this.$router.push('/address')
                }
            })
        }
    },
    created() {
        this.openList = this.$route.query.arrs;
        if (this.openList[3] == 0) {
            return this.value2 = false
        } else {
            return this.value2 = true
        }
    }
}
