import { apiGetNewsList, apiReadNewsRecord } from "@/api/News.js";
import back from "@/components/Botton/back.vue";
import Vue from "vue";

export default {
  name: "News",
  components: {
    back,
  },
  data() {
    return {
      newsList: [],
      Wid: "2.08rem",
      Hei: "1.64rem",

      // v-infinite-scroll 套件
      busy: false,
      push: false,
      IndexNum: 1,
      noMore: false,
    };
  },
  methods: {
    goBack() {
      this.$router.go(-1);
    },
    goNewsDetail(id) {
      let data = {};
      apiReadNewsRecord(data, true).then((res) => {});
      this.$router.push({ path: `./information/${id}` });
    },
    getNewsList() {
      let data = {};
      apiGetNewsList(data, true).then((res) => {
        this.newsList = res;
      });

      //actions的提取方法
      //    this.$store.dispatch('asyncGetNewsList');
    },

    // v-infinite-scroll 套件滑到底加載
    load() {
      this.busy = true;
      let data = {
        index: this.IndexNum,
        count: 10,
      };
      apiGetNewsList(data, true).then((res) => {
        if (res.news.length == 0) {
          this.busy = true;
          this.push = false;
          // this.noMore = true
        } else if (res.news.length > 0) {
          for (let i = 0; i < res.news.length; i++) {
            this.newsList.push(res.news[i]);
            this.busy = false;
          }
          this.IndexNum++;
        }
      });
    },
  },
  computed: {},
  mounted() {
    // const { $route: { params: { id } } } = this;
    // this.getNewsList()
  },
};
