import { apiGetUserAddress,apiAddEditMemAddress } from  '@/api/User.js'
import back from "@/components/Botton/back.vue";
    export default{
           components: {
                back
            },
        data() {
            return {
                ren: false,
                noMsg: '',
                addList: [],
            }
        },
        methods: {
            goHome() {
                // this.$router.push('/shop')
                this.$router.go(-1)
            },
            goEdit(id,name,phone,address,moren,timer) {       
                let arrs = []
                arrs.push(id,name,phone,address,moren,timer)
                this.$router.push({
                    path:'/editAddress',
                    query: {
                        arrs: arrs
                    }
                })
            },
            goAdd() {
                this.$router.push('/add')
            },
            getAddMsg() {
                let data = {}
                apiGetUserAddress(data, true).then(res => {
                    if (Math.abs(res.Code) >= 600) {
                        this.noMsg = true
                    } else {
                        this.addList = res.Data
                        if(this.addList.length > 0) {
                            this.noMsg = false
                        } else {
                            this.noMsg = true
                        }
                    }
                })
            }
        },
        created() {
            this.getAddMsg();
        }
    }
