    box-shadow: 6px 6px 6px #8c7d7f;
    .hong-span {
      font-size: 0.19rem /* 19/100 */;
    }
  }
  .lan {
    display: inline-block;
    width: 50%;
    height: 0.48rem;
    box-shadow: 1px 3px 5px #d7deeb;
    font-size: 0.2rem;
