import {
  apiGetRecommendMatchItem,
  apiGetBetResponse,
  apiBetRecommend,
  apiIsMaintenance
} from "@/api/match.js";
import Tinder from "vue-tinder";
import Vue from "vue";
import { Dialog , Image} from "vant";
import { randomBytes } from "crypto";
import { betSuccess, betPass } from "@/utils/medias";
import { apiGetUserInfo } from "@/api/User.js";
Vue.use(Dialog).use(Image);
export default {
  name: "homeCard",
  components: {
    Tinder
  },
  data() {
    return {
      isMaintenance: false,
      isSwipe: true,
      MatchList: [], //比赛集
      previousGame: null,
      playIdIndex: 0, //当前盘口位置
      pageIndex: 1,
      pageSize: 30,
      blank: false
    };
  },
  filters: {
    host(val) {
      if (val) {
        return "主";
      } else if (!val) {
        return "客";
      }
    }
  },
  computed: {},
  created() {
    this.getMatchList();
    let token = localStorage.getItem("token");
    if (token) {
      this.getUserInfo();
    }
  },
  methods: {
    getUserInfo() {
      let data = {};
      apiGetUserInfo(data, true).then(res => {
        localStorage.setItem("RankId", res.RankId);
        localStorage.setItem("nickName", res.NickName);
        localStorage.setItem("UImg", res.UImg);
      });
    },
    onSubmit(item) {
      if (this.$refs.tinder.queue.length == 1) {
        this.getMatchList();
        return;
      }
      // item.type： 结果，'like'：右滑, 'nope'：左滑, 'super'：上滑
      // item.key：  被移除卡片的 keyName
      // item.item： queue 中的子对象
      this.previousGame = item.item;
      if (item.type == "nope") {
        // 左滑音效
        betPass.play();
      } 
      if (item.type == "like") {
        let token = localStorage.getItem("token");
        if (token) {
          this.like(item.type);
          // 右滑音效
          betSuccess.play();
        } else {
          this.$router.push("/myself")
        }
      }
      
    },
    goGames() {
      this.$router.push("/gamesport");
    },
    //获取比赛信息
    getMatchList() {
      // let data = {
      //   index: this.pageIndex,
      //   count: this.pageSize
      // };
      apiGetRecommendMatchItem("").then(res => {
        // this.pageIndex++;
        if (res.length == 0) {
          //维护检查
          apiIsMaintenance().then(res => {
            this.isMaintenance = res.IsMaintenance;
          });
        } else {
          this.MatchList = res;
          // this.randomGames();
        }
        // if (this.pageIndex > res.TotalPages) {
        //   this.pageIndex = 1;
        // }
        if (this.MatchList.length == 0) {
          setTimeout(() => {
            this.blank = true;
          }, 500);
        } else {
          this.blank = false;
        }
      });
    },
    //右滑投注
    async like(choice) {
      console.log(choice)
      let data = {
        playId: this.previousGame.PlayId,
        betIntegral: this.previousGame.MinBetAmount
      };
      apiBetRecommend(data, true).then(res => {
        if (res.IsBetSucceed) {
          const m = this.$message({
            message: this.$t("BettingSuccessfulPrompt") + res.AvailableIntegral,
            type: "success",
            duration: 2000
          });
          setTimeout(() => m.close(), 2000);
        } else if (Math.abs(res.Code) >= 600) {
          const m = this.$message({
            message: res.Message,
            type: "error",
            duration: 2000
          });
          setTimeout(() => m.close(), 2000);
        }
      });
    },
    // 隨機組合
    randomGames() {
      if (this.MatchList) {
        this.MatchList.sort(function() {
          return 0.5 - Math.random();
        });
      }
    }

  }
};
